<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Moon Simulation</title>
    <style>
        body {
            margin: 0;
            display: flex;
            height: 100vh;
        }
        #app-container {
            display: flex;
        }
        #control-panel {
            width: 250px;
            padding: 20px;
            background-color: #f9fafb;
            box-shadow: 2px 0 5px rgba(0,0,0,0.1);
        }
        #visualization-area {
            flex-grow: 1;
            background-color: #e2e8f0;
            display: flex;
            justify-content: center;
            align-items: center;
        }
        #main-canvas {
            background-color: #ffffff;
        }
        .active {
            background-color: #3B82F6;
            color: white;
        }
        hr {
            border: none;
            border-top: 1px solid #d1d5db;
            margin: 10px 0;
        }
        label {
            margin-left: 5px;
        }
    </style>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.min.js"></script>
</head>
<body>
    <div id="app-container">
        <div id="control-panel">
            <h4>time</h4>
            <input type="range" id="slider-time" min="0" max="672" step="1" value="0">
            <div>
                <button id="btn-play-pause">▶</button>
                <button id="btn-step-back">«</button>
                <button id="btn-reset">⥀</button>
                <button id="btn-step-forward">»</button>
            </div>
            <p id="display-time">0 d 0 h</p>
            <hr>
            <h4>Moon's rotational period related to the current value</h4>
            <div id="rotation-selector">
                <button id="btn-rot-half">half</button>
                <button id="btn-rot-normal" class="active">normal</button>
                <button id="btn-rot-double">double</button>
                <button id="btn-rot-none">no rotation</button>
            </div>
            <hr>
            <div>
                <input type="checkbox" id="check-day-night">
                <label for="check-day-night">day/night</label>
            </div>
            <div>
                <input type="checkbox" id="check-near-side" checked>
                <label for="check-near-side">near side of the Moon</label>
            </div>
            <div>
                <input type="checkbox" id="check-earth-moon-line" checked>
                <label for="check-earth-moon-line">Earth-Moon line</label>
            </div>
            <div>
                <input type="checkbox" id="check-moon-only">
                <label for="check-moon-only">the Moon only</label>
            </div>
        </div>
        <div id="visualization-area">
            <canvas id="main-canvas"></canvas>
        </div>
    </div>

    <script>
        let time = 0;
        let isPlaying = false;
        let moonRotationMode = 'normal';
        let earthRadius = 80;
        let moonRadius = 25;
        let orbitRadius = 250;

        function setup() {
            const canvas = createCanvas(windowWidth - 250, windowHeight);
            canvas.parent('main-canvas');
            noLoop();
        }

        function draw() {
            clear();
            translate(width / 2, height / 2);

            let earthRotation = (time / 24) * TWO_PI;
            let moonOrbit = (time / 672) * TWO_PI;
            let moonRotation = moonOrbit;

            if (moonRotationMode === 'half') {
                moonRotation = (time / 1344) * TWO_PI;
            } else if (moonRotationMode === 'double') {
                moonRotation = (time / 336) * TWO_PI;
            } else if (moonRotationMode === 'none') {
                moonRotation = 0;
            }

            if (!document.getElementById('check-moon-only').checked) {
                // Draw Earth
                fill('#3B82F6');
                ellipse(0, 0, earthRadius * 2, earthRadius * 2);

                // Draw North Pole
                fill('white');
                ellipse(0, -earthRadius + 5, 10, 10);

                // Draw Simplified Landmasses
                fill('#22C55E');
                beginShape();
                vertex(-earthRadius + 20, -earthRadius + 20);
                vertex(-earthRadius + 40, -earthRadius + 30);
                vertex(-earthRadius + 50, -earthRadius + 10);
                endShape(CLOSE);

                beginShape();
                vertex(earthRadius - 40, earthRadius - 30);
                vertex(earthRadius - 30, earthRadius - 50);
                vertex(earthRadius - 20, earthRadius - 40);
                endShape(CLOSE);

                // Rotate Earth
                rotate(earthRotation);

                // Draw Stick Figure
                stroke('red');
                strokeWeight(2);
                point(0, -earthRadius + 15);
                line(0, -earthRadius + 15, 0, -earthRadius + 30);
                line(0, -earthRadius + 20, -10, -earthRadius + 25);
                line(0, -earthRadius + 20, 10, -earthRadius + 25);
                line(0, -earthRadius + 30, -10, -earthRadius + 40);
                line(0, -earthRadius + 30, 10, -earthRadius + 40);

                // Day/Night Terminator
                if (document.getElementById('check-day-night').checked) {
                    noFill();
                    stroke('black');
                    strokeWeight(2);
                    strokeCap(SQUARE);
                    strokeJoin(BEVEL);
                    stroke(0, 128);
                    arc(0, 0, earthRadius * 2, earthRadius * 2, PI, 0, OPEN);
                }

                // Reset Rotation
                rotate(-earthRotation);

                // Draw Earth-Moon Line
                if (document.getElementById('check-earth-moon-line').checked) {
                    stroke('black');
                    strokeWeight(1);
                    strokeDashArray([5, 5]);
                    line(0, 0, orbitRadius * cos(moonOrbit - PI / 2), orbitRadius * sin(moonOrbit - PI / 2));
                }
            }

            // Calculate Moon Position
            let mx = orbitRadius * cos(moonOrbit - PI / 2);
            let my = orbitRadius * sin(moonOrbit - PI / 2);

            if (document.getElementById('check-moon-only').checked) {
                mx = 0;
                my = 0;
            }

            push();
            translate(mx, my);

            // Draw Moon
            fill('#808080');
            ellipse(0, 0, moonRadius * 2, moonRadius * 2);

            // Near Side Marker
            if (document.getElementById('check-near-side').checked) {
                rotate(moonRotation);
                fill('#22C55E');
                arc(0, 0, moonRadius * 2, moonRadius * 2, 0, PI, OPEN);
                stroke('#D946EF');
                strokeWeight(2);
                point(0, -moonRadius + 5);
                line(0, -moonRadius + 10, -5, -moonRadius + 15);
                line(0, -moonRadius + 10, 5, -moonRadius + 15);
                line(0, -moonRadius + 10, 0, -moonRadius + 20);
                rotate(-moonRotation);
            }

            // Day/Night Terminator
            if (document.getElementById('check-day-night').checked) {
                noFill();
                stroke('black');
                strokeWeight(2);
                strokeCap(SQUARE);
                strokeJoin(BEVEL);
                stroke(0, 128);
                arc(0, 0, moonRadius * 2, moonRadius * 2, PI, 0, OPEN);
            }

            pop();

            // Update Time Display
            document.getElementById('display-time').innerText = `${Math.floor(time / 24)} d ${time % 24} h`;
        }

        document.getElementById('slider-time').addEventListener('input', () => {
            time = parseInt(document.getElementById('slider-time').value);
            redraw();
        });

        document.getElementById('btn-play-pause').addEventListener('click', () => {
            if (isPlaying) {
                noLoop();
                document.getElementById('btn-play-pause').innerText = '▶';
            } else {
                loop();
                document.getElementById('btn-play-pause').innerText = '❚❚';
            }
            isPlaying = !isPlaying;
        });

        document.getElementById('btn-step-back').addEventListener('click', () => {
            time = Math.max(0, time - 1);
            document.getElementById('slider-time').value = time;
            redraw();
        });

        document.getElementById('btn-reset').addEventListener('click', () => {
            time = 0;
            document.getElementById('slider-time').value = time;
            redraw();
        });

        document.getElementById('btn-step-forward').addEventListener('click', () => {
            time = Math.min(672, time + 1);
            document.getElementById('slider-time').value = time;
            redraw();
        });

        document.querySelectorAll('#rotation-selector button').forEach(button => {
            button.addEventListener('click', () => {
                document.querySelectorAll('#rotation-selector button').forEach(btn => btn.classList.remove('active'));
                button.classList.add('active');
                moonRotationMode = button.id.replace('btn-rot-', '');
                redraw();
            });
        });

        document.querySelectorAll('input[type="checkbox"]').forEach(checkbox => {
            checkbox.addEventListener('change', () => {
                redraw();
            });
        });
    </script>
</body>
</html>
