    <script>
        const ν₀ = 1000; // band origin in cm⁻¹
        const B_upper = 0.98; // B' in cm⁻¹
        const A_upper = 1.95; // A' in cm⁻¹
        const B_lower = 1.0; // B" in cm⁻¹
        const A_lower = 2.0; // A" in cm⁻¹
        const zeta = 0.2; // Coriolis coupling
        const T = 300; // Temperature in K
        const k = 0.695; // Boltzmann constant in cm⁻¹/K
        const J_max = 60; // Maximum J value to calculate

        const viewSelectorGroup = document.getElementById('view-selector-group');
        const kSelectorGroup = document.getElementById('k-selector-group');
        const subbandTypeGroup = document.getElementById('subband-type-group');
        const branchSelectorGroup = document.getElementById('branch-selector-group');

        const radioViewFull = document.getElementById('radio-view-full');
        const radioViewSub = document.getElementById('radio-view-sub');

        const radioK0 = document.getElementById('radio-k-0');
        const radioSubbandFull = document.getElementById('radio-subband-full');

        const sliderXLower = document.getElementById('slider-x-lower');
        const sliderXUpper = document.getElementById('slider-x-upper');
        const spanXLowerValue = document.getElementById('span-x-lower-value');
        const spanXUpperValue = document.getElementById('span-x-upper-value');

        let currentView = 'full';
        let currentK = 0;
        let currentSubbandType = 'full';
        let currentBranch = 'all';

        function updatePlot() {
            const data = [];
            const layout = {
                title: '',
                xaxis: { title: 'wavenumber (cm⁻¹)', range: [parseInt(sliderXLower.value), parseInt(sliderXUpper.value)] },
                yaxis: { title: 'absorbance', range: [0, 1.2] },
                showlegend: false,
                bargap: 0
            };

            if (currentView === 'full') {
                for (let K = 0; K <= 5; K++) {
                    for (let J = K; J <= J_max; J++) {
                        const P = calculateWavenumber(J, J - 1, K, B_upper, A_upper, zeta);
                        const Q = calculateWavenumber(J, J, K, B_upper, A_upper, zeta);
                        const R = calculateWavenumber(J, J + 1, K, B_upper, A_upper, zeta);

                        if (P >= parseInt(sliderXLower.value) && P <= parseInt(sliderXUpper.value)) {
                            data.push({ x: [P], y: [calculateIntensity(P)], type: 'bar', width: 0.1, marker: { color: 'black' } });
                        }
                        if (Q >= parseInt(sliderXLower.value) && Q <= parseInt(sliderXUpper.value)) {
                            data.push({ x: [Q], y: [calculateIntensity(Q)], type: 'bar', width: 0.1, marker: { color: 'black' } });
                        }
                        if (R >= parseInt(sliderXLower.value) && R <= parseInt(sliderXUpper.value)) {
                            data.push({ x: [R], y: [calculateIntensity(R)], type: 'bar', width: 0.1, marker: { color: 'black' } });
                        }
                    }
                }
                layout.title = 'Rovibronic Spectrum';
            } else if (currentView === 'sub') {
                for (let J = currentK; J <= J_max; J++) {
                    let P, Q, R;
                    if (currentSubbandType === 'full' || currentSubbandType === 'positive') {
                        P = calculateWavenumber(J, J - 1, currentK, B_upper, A_upper, zeta);
                        Q = calculateWavenumber(J, J, currentK, B_upper, A_upper, zeta);
                        R = calculateWavenumber(J, J + 1, currentK, B_upper, A_upper, zeta);

                        if (P >= parseInt(sliderXLower.value) && P <= parseInt(sliderXUpper.value)) {
                            if ((currentBranch === 'all' || currentBranch === 'P')) {
                                data.push({ x: [P], y: [calculateIntensity(P)], type: 'bar', width: 0.1, marker: { color: 'red' } });
                            }
                        }
                        if (Q >= parseInt(sliderXLower.value) && Q <= parseInt(sliderXUpper.value)) {
                            if ((currentBranch === 'all' || currentBranch === 'Q')) {
                                data.push({ x: [Q], y: [calculateIntensity(Q)], type: 'bar', width: 0.1, marker: { color: 'red' } });
                            }
                        }
                        if (R >= parseInt(sliderXLower.value) && R <= parseInt(sliderXUpper.value)) {
                            if ((currentBranch === 'all' || currentBranch === 'R')) {
                                data.push({ x: [R], y: [calculateIntensity(R)], type: 'bar', width: 0.1, marker: { color: 'red' } });
                            }
                        }
                    }
                    if (currentSubbandType === 'full' || currentSubbandType === 'negative') {
                        P = calculateWavenumber(J, J + 1, currentK, B_lower, A_lower, zeta);
                        Q = calculateWavenumber(J, J, currentK, B_lower, A_lower, zeta);
                        R = calculateWavenumber(J, J - 1, currentK, B_lower, A_lower, zeta);

                        if (P >= parseInt(sliderXLower.value) && P <= parseInt(sliderXUpper.value)) {
                            if ((currentBranch === 'all' || currentBranch === 'P')) {
                                data.push({ x: [P], y: [calculateIntensity(P)], type: 'bar', width: 0.1, marker: { color: 'blue' } });
                            }
                        }
                        if (Q >= parseInt(sliderXLower.value) && Q <= parseInt(sliderXUpper.value)) {
                            if ((currentBranch === 'all' || currentBranch === 'Q')) {
                                data.push({ x: [Q], y: [calculateIntensity(Q)], type: 'bar', width: 0.1, marker: { color: 'blue' } });
                            }
                        }
                        if (R >= parseInt(sliderXLower.value) && R <= parseInt(sliderXUpper.value)) {
                            if ((currentBranch === 'all' || currentBranch === 'R')) {
                                data.push({ x: [R], y: [calculateIntensity(R)], type: 'bar', width: 0.1, marker: { color: 'blue' } });
                            }
                        }
                    }
                }

                if (currentSubbandType === 'full') {
                    layout.title = `K = ${currentK} sub-band`;
                } else {
                    layout.title = `K = ${currentK} sub-band\n"${currentSubbandType}" sub-band`;
                }
            }

            Plotly.newPlot('plot-div', data, layout);
        }

        function calculateWavenumber(J, J_prime, K, B, A, zeta) {
            return ν₀ + B * (J * (J + 1) - J_prime * (J_prime + 1)) + A * (J * (J + 1) * (J * (J + 1) - 3) / 3 - J_prime * (J_prime + 1) * (J_prime * (J_prime + 1) - 3) / 3) + zeta * (J * (J + 1) - J_prime * (J_prime + 1));
        }

        function calculateIntensity(wavenumber) {
            return Math.exp(-wavenumber / (k * T));
        }

        function toggleView(view) {
            currentView = view;
            if (view === 'full') {
                kSelectorGroup.classList.add('disabled');
                subbandTypeGroup.classList.add('disabled');
                branchSelectorGroup.classList.add('hidden');
            } else if (view === 'sub') {
                kSelectorGroup.classList.remove('disabled');
                subbandTypeGroup.classList.remove('disabled');
                toggleSubbandType(currentSubbandType);
            }
            updatePlot();
        }

        function toggleK(k) {
            currentK = parseInt(k);
            if (k === 0) {
                document.getElementById('radio-subband-negative').disabled = true;
            } else {
                document.getElementById('radio-subband-negative').disabled = false;
            }
            updatePlot();
        }

        function toggleSubbandType(type) {
            currentSubbandType = type;
            if (type === 'full') {
                branchSelectorGroup.classList.add('hidden');
            } else {
                branchSelectorGroup.classList.remove('hidden');
            }
            updatePlot();
        }

        function toggleBranch(branch) {
            currentBranch = branch;
            updatePlot();
        }

        function updateSliderValues() {
            const lowerValue = parseInt(sliderXLower.value);
            const upperValue = parseInt(sliderXUpper.value);

            if (lowerValue >= upperValue) {
                sliderXUpper.value = lowerValue + 1;
            } else if (upperValue <= lowerValue) {
                sliderXLower.value = upperValue - 1;
            }

            spanXLowerValue.textContent = sliderXLower.value;
            spanXUpperValue.textContent = sliderXUpper.value;

            updatePlot();
        }

        viewSelectorGroup.addEventListener('click', (event) => {
            if (event.target.classList.contains('toggle-button')) {
                document.querySelectorAll('.toggle-button').forEach(button => button.classList.remove('selected'));
                event.target.classList.add('selected');
                if (event.target.dataset.target === 'radio-view-full') {
                    radioViewFull.checked = true;
                    toggleView('full');
                } else if (event.target.dataset.target === 'radio-view-sub') {
                    radioViewSub.checked = true;
                    toggleView('sub');
                }
            }
        });

        kSelectorGroup.addEventListener('change', (event) => {
            if (event.target.name === 'k-selector') {
                toggleK(event.target.value);
            }
        });

        subbandTypeGroup.addEventListener('change', (event) => {
            if (event.target.name === 'subband-type') {
                toggleSubbandType(event.target.value);
            }
        });

        branchSelectorGroup.addEventListener('change', (event) => {
            if (event.target.name === 'branch-selector') {
                toggleBranch(event.target.value);
            }
        });

        sliderXLower.addEventListener('input', updateSliderValues);
        sliderXUpper.addEventListener('input', updateSliderValues);

        // Initial plot
        updatePlot();
    </script>
</body>
</html>
