    <script type="module">
        import * as THREE from 'three';
        import { OrbitControls } from 'three/addons/controls/OrbitControls.js';

        let currentWaveType = 'P';
        let scene, camera, renderer, controls;
        let strike = 180, dip = 60, rake = 90;
        let radiationPattern, boxHelper;

        function init() {
            scene = new THREE.Scene();
            scene.background = new THREE.Color(0xf0f0f0);

            camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
            camera.position.z = 200;

            renderer = new THREE.WebGLRenderer({ antialias: true });
            renderer.setSize(window.innerWidth, window.innerHeight);
            document.getElementById('canvas-container').appendChild(renderer.domElement);

            controls = new OrbitControls(camera, renderer.domElement);
            controls.enableDamping = true;
            controls.dampingFactor = 0.25;
            controls.enableZoom = true;

            const ambientLight = new THREE.AmbientLight(0x404040);
            scene.add(ambientLight);

            const directionalLight1 = new THREE.DirectionalLight(0xffffff, 0.5);
            directionalLight1.position.set(1, 1, 1).normalize();
            scene.add(directionalLight1);

            const directionalLight2 = new THREE.DirectionalLight(0xffffff, 0.5);
            directionalLight2.position.set(-1, -1, -1).normalize();
            scene.add(directionalLight2);

            createRadiationPattern(currentWaveType);
            createCompass();
            createUpArrow();

            window.addEventListener('resize', onWindowResize, false);

            document.getElementById('slider-strike').addEventListener('input', updateStrike);
            document.getElementById('slider-dip').addEventListener('input', updateDip);
            document.getElementById('slider-rake').addEventListener('input', updateRake);
            document.getElementById('btn-p').addEventListener('click', () => changeWaveType('P'));
            document.getElementById('btn-sv').addEventListener('click', () => changeWaveType('SV'));
            document.getElementById('btn-sh').addEventListener('click', () => changeWaveType('SH'));
            document.getElementById('checkbox-box').addEventListener('change', toggleBoundingBox);

            animate();
        }

        function createRadiationPattern(waveType) {
            if (radiationPattern) {
                scene.remove(radiationPattern);
            }

            const material = new THREE.MeshStandardMaterial({ color: 0xD2B48C, side: THREE.DoubleSide });

            const geometry = new THREE.ParametricGeometry((u, v, target) => {
                const phi = u * Math.PI * 2;
                const theta = v * Math.PI;
                let r;
                switch (waveType) {
                    case 'P':
                        r = Math.abs(Math.sin(2 * theta) * Math.sin(phi));
                        break;
                    case 'SV':
                        r = Math.abs(Math.cos(2 * theta) * Math.sin(phi));
                        break;
                    case 'SH':
                        r = Math.abs(Math.cos(theta) * Math.cos(phi));
                        break;
                }
                target.x = r * Math.sin(theta) * Math.cos(phi);
                target.y = r * Math.sin(theta) * Math.sin(phi);
                target.z = r * Math.cos(theta);
            }, 100, 100);

            radiationPattern = new THREE.Mesh(geometry, material);
            radiationPattern.rotation.order = 'ZYX';
            radiationPattern.rotation.z = THREE.MathUtils.degToRad(strike);
            radiationPattern.rotation.x = THREE.MathUtils.degToRad(dip);
            radiationPattern.rotation.y = THREE.MathUtils.degToRad(rake);

            scene.add(radiationPattern);

            if (boxHelper) {
                boxHelper.dispose();
                boxHelper = new THREE.BoxHelper(radiationPattern, 0x00ff00);
                boxHelper.visible = document.getElementById('checkbox-box').checked;
                scene.add(boxHelper);
            }
        }

        function createCompass() {
            const compassGroup = new THREE.Group();

            const circleGeometry = new THREE.CircleGeometry(100, 120);
            circleGeometry.vertices.shift(); // remove center vertex
            const circleMaterial = new THREE.LineDashedMaterial({ color: 0x000000, dashSize: 5, gapSize: 5 });
            const circle = new THREE.Line(circleGeometry, circleMaterial);
            circle.computeLineDistances();
            compassGroup.add(circle);

            const labels = ['N', 'E', 'S', 'W'];
            for (let i = 0; i < labels.length; i++) {
                const label = createLabel(labels[i]);
                label.position.set(110 * Math.cos(i * Math.PI / 2), 110 * Math.sin(i * Math.PI / 2), 0);
                compassGroup.add(label);
            }

            for (let i = 0; i < 12; i++) {
                if (i % 3 !== 0) {
                    const degreeLabel = createLabel((i * 30).toString());
                    degreeLabel.scale.set(0.5, 0.5, 0.5);
                    degreeLabel.position.set(100 * Math.cos(i * Math.PI / 6), 100 * Math.sin(i * Math.PI / 6), 0);
                    compassGroup.add(degreeLabel);
                }
            }

            compassGroup.rotation.x = -Math.PI / 2;
            scene.add(compassGroup);
        }

        function createLabel(text) {
            const canvas = document.createElement('canvas');
            const context = canvas.getContext('2d');
            context.font = 'Bold 20px Arial';
            const metrics = context.measureText(text);
            const textWidth = metrics.width;
            const textHeight = 20;
            canvas.width = textWidth;
            canvas.height = textHeight;
            context.font = 'Bold 20px Arial';
            context.fillStyle = '#000';
            context.fillText(text, 0, textHeight / 2 + 5);

            const texture = new THREE.Texture(canvas);
            texture.needsUpdate = true;

            const material = new THREE.SpriteMaterial({ map: texture });
            const label = new THREE.Sprite(material);
            return label;
        }

        function createUpArrow() {
            const arrow = new THREE.ArrowHelper(new THREE.Vector3(0, 0, 1), new THREE.Vector3(0, 0, 0), 20, 0x000000);
            scene.add(arrow);

            const upLabel = createLabel('U');
            upLabel.scale.set(0.5, 0.5, 0.5);
            upLabel.position.set(0, 0, 21);
            scene.add(upLabel);
        }

        function updateStrike(event) {
            strike = parseFloat(event.target.value);
            document.getElementById('strike-value').textContent = strike;
            radiationPattern.rotation.z = THREE.MathUtils.degToRad(strike);
            renderer.render(scene, camera);
        }

        function updateDip(event) {
            dip = parseFloat(event.target.value);
            document.getElementById('dip-value').textContent = dip;
            radiationPattern.rotation.x = THREE.MathUtils.degToRad(dip);
            renderer.render(scene, camera);
        }

        function updateRake(event) {
            rake = parseFloat(event.target.value);
            document.getElementById('rake-value').textContent = rake;
            radiationPattern.rotation.y = THREE.MathUtils.degToRad(rake);
            renderer.render(scene, camera);
        }

        function changeWaveType(type) {
            document.querySelectorAll('#control-panel button').forEach(btn => btn.classList.remove('active'));
            document.getElementById(`btn-${type.toLowerCase()}`).classList.add('active');
            currentWaveType = type;
            createRadiationPattern(currentWaveType);
        }

        function toggleBoundingBox(event) {
            if (boxHelper) {
                boxHelper.visible = event.target.checked;
                renderer.render(scene, camera);
            }
        }

        function onWindowResize() {
            camera.aspect = window.innerWidth / window.innerHeight;
            camera.updateProjectionMatrix();
            renderer.setSize(window.innerWidth, window.innerHeight);
        }

        function animate() {
            requestAnimationFrame(animate);
            controls.update();
            renderer.render(scene, camera);
        }

        init();
        MathJax.typeset();
    </script>
</body>
</html>
