            if (draggingHandle) {
                let container = draggingHandle.parentElement;
                let rect = container.getBoundingClientRect();
                let containerWidth = rect.width;
                let containerHeight = rect.height;

                let offsetX = constrain(mouseX - rect.left, 0, containerWidth);
                let offsetY = constrain(mouseY - rect.top, 0, containerHeight);

                draggingHandle.style.left = (offsetX / containerWidth * 100) + '%';
                draggingHandle.style.top = (offsetY / containerHeight * 100) + '%';

                if (draggingHandle === document.getElementById('joystick-handle1')) {
                    share1Pos.x = map(offsetX, 0, containerWidth, 0, width);
                    share1Pos.y = map(offsetY, 0, containerHeight, 0, height);
