    </style>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
</head>
<body>
    <div id="main-container">
        <div id="control-panel">
            <div id="rings-control-group">
                <label for="rings-radios">rings</label>
                <div>
                    <input type="radio" name="rings" value="1" id="rings-1" checked>
                    <label for="rings-1">1</label>
                    <input type="radio" name="rings" value="2" id="rings-2">
                    <label for="rings-2">2</label>
                    <input type="radio" name="rings" value="3" id="rings-3">
                    <label for="rings-3">3</label>
                    <input type="radio" name="rings" value="4" id="rings-4">
                    <label for="rings-4">4</label>
                    <input type="radio" name="rings" value="5" id="rings-5">
                    <label for="rings-5">5</label>
                    <input type="radio" name="rings" value="6" id="rings-6">
                    <label for="rings-6">6</label>
                    <input type="radio" name="rings" value="7" id="rings-7">
                    <label for="rings-7">7</label>
                </div>
            </div>
            <div id="circles-per-ring-control-group">
                <label for="cpr-radios">circles per ring</label>
                <div>
                    <input type="radio" name="cpr" value="4" id="cpr-4">
                    <label for="cpr-4">4</label>
                    <input type="radio" name="cpr" value="6" id="cpr-6">
                    <label for="cpr-6">6</label>
                    <input type="radio" name="cpr" value="8" id="cpr-8" checked>
                    <label for="cpr-8">8</label>
                    <input type="radio" name="cpr" value="10" id="cpr-10">
                    <label for="cpr-10">10</label>
                    <input type="radio" name="cpr" value="12" id="cpr-12">
                    <label for="cpr-12">12</label>
                    <input type="radio" name="cpr" value="14" id="cpr-14">
                    <label for="cpr-14">14</label>
                </div>
            </div>
            <div id="inverse-control-group">
                <label for="checkbox-inverse">inverse</label>
                <input type="checkbox" id="checkbox-inverse">
            </div>
        </div>
        <div id="canvas-container">
            <canvas id="p5-canvas"></canvas>
        </div>
    </div>

    <script>
        let locators = [createVector(-50, 0), createVector(50, 0)];
        let draggedLocator = null;

        function setup() {
            const canvasSize = 550;
            createCanvas(canvasSize, canvasSize);
            angleMode(RADIANS);
            translate(width / 2, height / 2);

            document.querySelectorAll('input[name="rings"]').forEach(radio => radio.addEventListener('change', draw));
            document.querySelectorAll('input[name="cpr"]').forEach(radio => radio.addEventListener('change', draw));
            document.getElementById('checkbox-inverse').addEventListener('change', draw);
        }

        function draw() {
            background('#C1D4E6');
            const numRings = parseInt(document.querySelector('input[name="rings"]:checked').value);
            const circlesPerRing = parseInt(document.querySelector('input[name="cpr"]:checked').value);
            const inverseMode = document.getElementById('checkbox-inverse').checked;

            if (!inverseMode) {
                drawNormalPattern(numRings, circlesPerRing);
            } else {
                drawInversePattern(numRings, circlesPerRing);
            }
        }

        function drawNormalPattern(numRings, circlesPerRing) {
            const R_max = width / 2 * 0.9;
            const k = (1 - sin(PI / circlesPerRing)) / (1 + sin(PI / circlesPerRing));
            const r = R_max * sin(PI / circlesPerRing) / (1 + sin(PI / circlesPerRing));

            for (let i = 0; i < numRings; i++) {
                const R_c = R_max * pow(k, i);
                for (let j = 0; j < circlesPerRing; j++) {
                    const angle = j * 2 * PI / circlesPerRing;
                    const x = R_c * cos(angle);
                    const y = R_c * sin(angle);
                    if (i === numRings - 1) {
                        stroke('#A0A0A0');
                        line(0, 0, x, y);
                    }
                    fill('#8C929D');
                    stroke(0);
                    ellipse(x, y, r * 2 * pow(k, i));
                }
            }
        }

        function drawInversePattern(numRings, circlesPerRing) {
            const R_max = width / 2 * 0.9;
            const k = (1 - sin(PI / circlesPerRing)) / (1 + sin(PI / circlesPerRing));
            const r = R_max * sin(PI / circlesPerRing) / (1 + sin(PI / circlesPerRing));

            const C_inv = p5.Vector.add(locators[0], locators[1]).mult(0.5);
            const R_inv = p5.Vector.dist(locators[0], locators[1]) / 2;

            noFill();
            stroke('#FF0000');
            line(locators[0].x, locators[0].y, locators[1].x, locators[1].y);

            fill('#FF0000');
            ellipse(locators[0].x, locators[0].y, 8);
            ellipse(locators[1].x, locators[1].y, 8);

            fill('rgba(255, 0, 0, 0.3)');
            stroke(0);
            ellipse(C_inv.x, C_inv.y, R_inv * 2);

            for (let i = 0; i < numRings; i++) {
                const R_c = R_max * pow(k, i);
                for (let j = 0; j < circlesPerRing; j++) {
                    const angle = j * 2 * PI / circlesPerRing;
                    const x = R_c * cos(angle);
                    const y = R_c * sin(angle);
                    const C_orig = createVector(x, y);
                    const d = p5.Vector.dist(C_inv, C_orig);

                    fill('rgba(220, 100, 220, 0.5)');
                    stroke(0);

                    if (abs(d - r * pow(k, i)) < 1) {
                        const lineDist = R_inv * R_inv / (2 * d);
                        const normal = p5.Vector.sub(C_inv, C_orig).normalize().rotate(HALF_PI);
                        const point1 = p5.Vector.mult(normal, lineDist).add(C_inv);
                        const point2 = p5.Vector.mult(normal, -lineDist).add(C_inv);
                        line(point1.x, point1.y, point2.x, point2.y);
                    } else {
                        const factor = R_inv * R_inv / (d * d - r * r * pow(k, i) * pow(k, i));
                        const C_new = p5.Vector.sub(C_orig, C_inv).mult(factor).add(C_inv);
                        const r_new = abs(factor) * r * pow(k, i);
                        ellipse(C_new.x, C_new.y, r_new * 2);
                    }
                }
            }
        }

        function mousePressed() {
            if (document.getElementById('checkbox-inverse').checked) {
                locators.forEach((locator, index) => {
                    if (dist(mouseX - width / 2, mouseY - height / 2, locator.x, locator.y) < 10) {
                        draggedLocator = index;
                    }
                });
            }
        }

        function mouseDragged() {
            if (draggedLocator !== null) {
                locators[draggedLocator].set(mouseX - width / 2, mouseY - height / 2);
                draw();
            }
        }

        function mouseReleased() {
            draggedLocator = null;
        }
    </script>
</body>
</html>
