            }

            // Y-axis ticks and labels
            for (let i = 0; i <= 7; i++) {
                let y = map(i, 0, 7, canvasHeight - 50, 50);
                line(45, y, 55, y);
                text(i, 30, y + 5);
            }
        }

        function drawStrongAcidLine() {
            stroke('maroon');
            strokeWeight(2);
            let y = map(-logCa, 0, 7, canvasHeight - 50, 50);
            line(50, y, canvasWidth - 50, y);
        }

        function drawWeakAcidLine() {
            stroke('green');
            strokeWeight(2);
            let x1 = 50;
            let y1 = map(0.5 * (pKa - logCa), 0, 7, canvasHeight - 50, 50);
            let x2 = canvasWidth - 50;
            let y2 = map(0.5 * (-4 - logCa), 0, 7, canvasHeight - 50, 50);
            line(x1, y1, x2, y2);
        }

        function drawExactPHCurve() {
            stroke('blue');
            strokeWeight(4);
            beginShape();
            for (let x = -4; x <= 7; x += 0.1) {
                let pH = calculateExactPH(x, logCa);
                let px = map(x, -4, 7, 50, canvasWidth - 50);
                let py = map(pH, 0, 7, canvasHeight - 50, 50);
                vertex(px, py);
            }
            endShape();
        }

        function drawIndicatorPoint() {
            fill('orange');
            stroke(0);
            strokeWeight(2);
            let pH = calculateExactPH(pKa, logCa);
            let px = map(pKa, -4, 7, 50, canvasWidth - 50);
            let py = map(pH, 0, 7, canvasHeight - 50, 50);
            circle(px, py, 10);
        }

        function calculateExactPH(pKa, logCa) {
            let Ka = Math.pow(10, -pKa);
            let Ca = Math.pow(10, logCa);
            let a = 1;
            let b = Ka;
            let c = -Ka * Ca;
            let discriminant = b * b - 4 * a * c;
            let HPlus = (-b + Math.sqrt(discriminant)) / (2 * a);
            return -Math.log10(HPlus);
        }

        document.getElementById('slider-logCa').addEventListener('input', function() {
            logCa = parseFloat(this.value);
            redraw();
        });

        document.getElementById('slider-pka').addEventListener('input', function() {
            pKa = parseFloat(this.value);
            redraw();
        });
    </script>
</body>
</html>
