            background-color: #fff;
        }
    </style>
</head>
<body>
    <div id="main-container">
        <div id="control-panel">
            <div class="control-group">
                <label>search algorithm</label>
                <div>
                    <input type="radio" id="radio-dijkstra" name="algorithm" value="dijkstra" checked>
                    <label for="radio-dijkstra">Dijkstra</label>
                    <input type="radio" id="radio-a-star" name="algorithm" value="a-star">
                    <label for="radio-a-star">A*</label>
                </div>
            </div>
            <div class="control-group">
                <label for="slider-step">algorithm step</label>
                <div class="slider-container">
                    <input type="range" id="slider-step" min="1" max="1" value="1" step="1">
                    <span id="label-step">+ 1</span>
                </div>
            </div>
            <div class="control-group">
                <label>starting point</label>
                <div id="joystick-start" class="joystick-area">
                    <div id="handle-start" class="joystick-handle"></div>
                </div>
            </div>
            <div class="control-group">
                <label>end point</label>
                <div id="joystick-end" class="joystick-area">
                    <div id="handle-end" class="joystick-handle"></div>
                </div>
            </div>
            <div class="control-group">
                <label for="select-obstacles">obstacles</label>
                <select id="select-obstacles">
                    <option value="1-square">1 square</option>
                    <option value="2-squares">2 squares</option>
                    <option value="l-shaped-wall">L-shaped wall</option>
                </select>
            </div>
        </div>
        <div id="canvas-container">
            <!-- p5.js canvas will be inserted here by the script -->
        </div>
    </div>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
    <script>
        let gridWidth = 50;
        let gridHeight = 50;
        let cellSize;
        let start = { x: 0.15, y: 0.85 };
        let end = { x: 0.85, y: 0.15 };
        let obstacles = [];
        let history = [];
        let currentStep = 1;
        let algorithm = 'dijkstra';

        const obstacleLayouts = {
            '1-square': [[20, 20], [21, 20], [22, 20]],
            '2-squares': [[20, 20], [21, 20], [22, 20], [30, 30], [31, 30], [32, 30]],
            'l-shaped-wall': [
                [20, 20], [21, 20], [22, 20], [23, 20], [24, 20],
                [20, 21], [20, 22], [20, 23], [20, 24]
            ]
        };

        function setup() {
            const canvasContainer = document.getElementById('canvas-container');
            const containerWidth = canvasContainer.clientWidth;
            const containerHeight = canvasContainer.clientHeight;
            createCanvas(min(containerWidth, containerHeight), min(containerWidth, containerHeight));
            cellSize = width / gridWidth;
            updateObstacles();
            computePath();
            document.getElementById('slider-step').addEventListener('input', onSliderChange);
            document.getElementById('slider-step').addEventListener('change', onSliderChange);
            document.getElementById('radio-dijkstra').addEventListener('change', onAlgorithmChange);
            document.getElementById('radio-a-star').addEventListener('change', onAlgorithmChange);
            document.getElementById('select-obstacles').addEventListener('change', onObstacleChange);
            document.getElementById('joystick-start').addEventListener('mousedown', onJoystickMouseDown);
            document.getElementById('joystick-end').addEventListener('mousedown', onJoystickMouseDown);
        }

        function draw() {
            background(255);
            const frame = history[currentStep - 1];
            if (!frame) return;

            for (let y = 0; y < gridHeight; y++) {
                for (let x = 0; x < gridWidth; x++) {
                    const cell = frame.grid[y][x];
                    fill(cell.color);
                    rect(x * cellSize, y * cellSize, cellSize, cellSize);
                }
            }

            fill('#34A853');
            noStroke();
            rect(start.x * gridWidth * cellSize, start.y * gridHeight * cellSize, cellSize, cellSize);
            rect(end.x * gridWidth * cellSize, end.y * gridHeight * cellSize, cellSize, cellSize);

            if (currentStep === history.length) {
                stroke('#FF0000');
                strokeWeight(3);
                noFill();
                beginShape();
                for (const point of frame.path) {
                    vertex(point.x * cellSize + cellSize / 2, point.y * cellSize + cellSize / 2);
                }
                endShape();
            }
        }

        function onSliderChange(event) {
            currentStep = parseInt(event.target.value);
            document.getElementById('label-step').textContent = `+ ${currentStep}`;
            redraw();
        }

        function onAlgorithmChange(event) {
            algorithm = event.target.value;
            computePath();
        }

        function onObstacleChange(event) {
            updateObstacles();
            computePath();
        }

        function updateObstacles() {
            const obstacleKey = document.getElementById('select-obstacles').value;
            obstacles = obstacleLayouts[obstacleKey] || [];
        }

        function onJoystickMouseDown(event) {
            const joystickId = event.target.id === 'handle-start' ? 'joystick-start' : 'joystick-end';
            const handleId = event.target.id === 'handle-start' ? 'handle-start' : 'handle-end';
            const joystickArea = document.getElementById(joystickId);
            const handle = document.getElementById(handleId);

            const rect = joystickArea.getBoundingClientRect();
            const startX = event.clientX - rect.left - handle.offsetWidth / 2;
            const startY = event.clientY - rect.top - handle.offsetHeight / 2;

            const onMouseMove = (event) => {
                const newX = constrain(event.clientX - rect.left - handle.offsetWidth / 2, 0, rect.width - handle.offsetWidth);
                const newY = constrain(event.clientY - rect.top - handle.offsetHeight / 2, 0, rect.height - handle.offsetHeight);
                handle.style.left = `${newX}px`;
                handle.style.top = `${newY}px`;

                if (joystickId === 'joystick-start') {
                    start = {
                        x: (newX + handle.offsetWidth / 2) / rect.width,
                        y: (newY + handle.offsetHeight / 2) / rect.height
                    };
                } else {
                    end = {
                        x: (newX + handle.offsetWidth / 2) / rect.width,
                        y: (newY + handle.offsetHeight / 2) / rect.height
                    };
                }
            };

            const onMouseUp = () => {
                document.removeEventListener('mousemove', onMouseMove);
                document.removeEventListener('mouseup', onMouseUp);
                computePath();
            };

            document.addEventListener('mousemove', onMouseMove);
            document.addEventListener('mouseup', onMouseUp);
        }

        function computePath() {
            const grid = Array.from({ length: gridHeight }, () => Array(gridWidth).fill({ color: '#FFFFFF' }));
            for (const obstacle of obstacles) {
                grid[obstacle[1]][obstacle[0]].color = '#000000';
            }

            const startNode = { x: Math.floor(start.x * gridWidth), y: Math.floor(start.y * gridHeight) };
            const endNode = { x: Math.floor(end.x * gridWidth), y: Math.floor(end.y * gridHeight) };

            history = [];
            if (algorithm === 'dijkstra') {
                dijkstra(grid, startNode, endNode);
            } else if (algorithm === 'a-star') {
                aStar(grid, startNode, endNode);
            }

            document.getElementById('slider-step').max = history.length;
            document.getElementById('slider-step').value = 1;
            currentStep = 1;
            redraw();
        }

        function dijkstra(grid, startNode, endNode) {
            const queue = new PriorityQueue();
            queue.enqueue(startNode, 0);
            const cameFrom = {};
            const costSoFar = {};
            cameFrom[startNode] = null;
            costSoFar[startNode] = 0;

            while (!queue.isEmpty()) {
                const current = queue.dequeue();

                if (current.x === endNode.x && current.y === endNode.y) break;

                for (const neighbor of getNeighbors(current)) {
                    const newCost = costSoFar[current] + getCost(current, neighbor);
                    if (!costSoFar[neighbor] || newCost < costSoFar[neighbor]) {
                        costSoFar[neighbor] = newCost;
                        queue.enqueue(neighbor, newCost);
                        cameFrom[neighbor] = current;
                    }
                }

                const frameGrid = JSON.parse(JSON.stringify(grid));
                frameGrid[current.y][current.x].color = '#0000CD';
                for (const node of queue.items) {
                    frameGrid[node.item.y][node.item.x].color = '#BDE0FE';
                }
                history.push({ grid: frameGrid, path: reconstructPath(cameFrom, current) });
            }
        }

        function aStar(grid, startNode, endNode) {
            const queue = new PriorityQueue();
            queue.enqueue(startNode, 0);
            const cameFrom = {};
            const costSoFar = {};
            cameFrom[startNode] = null;
            costSoFar[startNode] = 0;

            while (!queue.isEmpty()) {
                const current = queue.dequeue();

                if (current.x === endNode.x && current.y === endNode.y) break;

                for (const neighbor of getNeighbors(current)) {
                    const newCost = costSoFar[current] + getCost(current, neighbor);
                    if (!costSoFar[neighbor] || newCost < costSoFar[neighbor]) {
                        costSoFar[neighbor] = newCost;
                        const priority = newCost + heuristic(neighbor, endNode);
                        queue.enqueue(neighbor, priority);
                        cameFrom[neighbor] = current;
                    }
                }

                const frameGrid = JSON.parse(JSON.stringify(grid));
                frameGrid[current.y][current.x].color = '#0000CD';
                for (const node of queue.items) {
                    frameGrid[node.item.y][node.item.x].color = '#BDE0FE';
                }
                history.push({ grid: frameGrid, path: reconstructPath(cameFrom, current) });
            }
        }

        function heuristic(a, b) {
            return abs(a.x - b.x) + abs(a.y - b.y);
        }

        function getNeighbors(node) {
            const directions = [
                { x: -1, y: 0 }, { x: 1, y: 0 }, { x: 0, y: -1 }, { x: 0, y: 1 },
                { x: -1, y: -1 }, { x: 1, y: -1 }, { x: -1, y: 1 }, { x: 1, y: 1 }
            ];
            return directions.map(dir => ({ x: node.x + dir.x, y: node.y + dir.y }))
                .filter(n => n.x >= 0 && n.x < gridWidth && n.y >= 0 && n.y < gridHeight && !isObstacle(n));
        }

        function getCost(fromNode, toNode) {
            return (fromNode.x === toNode.x || fromNode.y === toNode.y) ? 1 : sqrt(2);
        }

        function isObstacle(node) {
            return obstacles.some(obstacle => obstacle[0] === node.x && obstacle[1] === node.y);
        }

        function reconstructPath(cameFrom, current) {
            const path = [current];
            while (cameFrom[current]) {
                current = cameFrom[current];
                path.unshift(current);
            }
            return path;
        }

        class PriorityQueue {
            constructor() {
                this.items = [];
            }

            enqueue(element, priority) {
                this.items.push({ item: element, priority: priority });
                this.items.sort((a, b) => a.priority - b.priority);
            }

            dequeue() {
                return this.items.shift().item;
            }

            isEmpty() {
                return this.items.length === 0;
            }
        }
    </script>
</body>
</html>
