<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Electric Field Simulation</title>
    <style>
        body {
            font-family: Arial, sans-serif;
            margin: 0;
            padding: 0;
            display: flex;
            flex-direction: column;
            align-items: center;
        }
        #app-container {
            display: flex;
            flex-direction: column;
            width: 100%;
            max-width: 1200px;
            margin: 20px;
        }
        #controls-container {
            display: flex;
            justify-content: space-around;
            margin-bottom: 20px;
        }
        .slider-group {
            display: flex;
            flex-direction: column;
            align-items: center;
        }
        #main-content {
            display: flex;
            justify-content: space-between;
        }
        #canvas-container {
            width: 600px;
            height: 500px;
        }
        #info-panel {
            width: 300px;
            padding: 20px;
            border: 1px solid #ccc;
            box-shadow: 2px 2px 12px rgba(0, 0, 0, 0.1);
        }
        #info-panel p {
            margin: 5px 0;
        }
    </style>
    <script src="https://cdn.jsdelivr.net/npm/p5@1.9.0/lib/p5.js"></script>
</head>
<body>
    <div id="app-container">
        <div id="controls-container">
            <div class="slider-group">
                <label for="slider-source-charge">source charge</label>
                <input type="range" id="slider-source-charge" min="-5" max="5" step="0.1" value="2.0">
                <span id="label-source-charge">2.0</span>
            </div>
            <div class="slider-group">
                <label for="slider-test-charge">charge</label>
                <input type="range" id="slider-test-charge" min="-0.2" max="0.2" step="0.0005" value="-0.0845">
                <span id="label-test-charge">-0.0845</span>
            </div>
        </div>
        <div id="main-content">
            <div id="canvas-container"></div>
            <div id="info-panel">
                <p id="display-E-mag">|E| = 0.153 N/C</p>
                <p id="display-Ex">E<sub>x</sub> component = 0.085 N/C</p>
                <p id="display-Ey">E<sub>y</sub> component = 0.127 N/C</p>
                <p id="display-x-pos">x position = 2.00 m</p>
                <p id="display-y-pos">y position = 3.00 m</p>
                <p id="display-force">Electric force = 0.013 N</p>
            </div>
        </div>
    </div>

    <script>
        let Q = 2.0; // Source charge
        let q = -0.0845; // Test charge
        let k = 1.0; // Coulomb's constant
        let scale_factor = 40; // Scale factor for drawing
        let x_pos = 2; // Initial x position of test charge
        let y_pos = 3; // Initial y position of test charge
        let dragging = false;

        function setup() {
            createCanvas(600, 500).parent('canvas-container');
            translate(width / 2, height / 2);
            scale(1, -1);

            document.getElementById('slider-source-charge').addEventListener('input', updateSourceCharge);
            document.getElementById('slider-test-charge').addEventListener('input', updateTestCharge);
        }

        function draw() {
            background(255);
            drawGrid();
            drawAxes();
            drawSourceCharge();
            drawTestChargeLocator();
            calculateAndUpdateVectors();
        }

        function drawGrid() {
            stroke(200);
            for (let i = -7; i <= 7; i++) {
                line(i * scale_factor, -7 * scale_factor, i * scale_factor, 7 * scale_factor);
                line(-7 * scale_factor, i * scale_factor, 7 * scale_factor, i * scale_factor);
            }
        }

        function drawAxes() {
            stroke(0);
            line(-7 * scale_factor, 0, 7 * scale_factor, 0);
            line(0, -7 * scale_factor, 0, 7 * scale_factor);
            textSize(16);
            textAlign(CENTER, CENTER);
            for (let i = -7; i <= 7; i++) {
                if (i !== 0) {
                    text(i, i * scale_factor, 15);
                    text(i, 15, -i * scale_factor);
                }
            }
        }

        function drawSourceCharge() {
            fill(0, 0, 255);
            noStroke();
            ellipse(0, 0, 10, 10);
        }

        function drawTestChargeLocator() {
            fill(0);
            noStroke();
            ellipse(x_pos * scale_factor, y_pos * scale_factor, 10, 10);
        }

        function calculateAndUpdateVectors() {
            let r_vec = createVector(x_pos, y_pos);
            let r = r_vec.mag();
            let r_hat = r_vec.copy().normalize();

            let E_vec = r_hat.copy().mult(k * Q / (r * r));
            let F_vec = E_vec.copy().mult(q);

            drawArrow(createVector(x_pos * scale_factor, y_pos * scale_factor), E_vec.copy().mult(scale_factor * 400), color(255, 0, 0));
            if (q !== 0) {
                drawArrow(createVector(x_pos * scale_factor, y_pos * scale_factor), F_vec.copy().mult(scale_factor * 400), color(0, 0, 255));
            }

            updateInfoPanel(E_vec, F_vec);
        }

        function drawArrow(base, vector, col) {
            push();
            stroke(col);
            strokeWeight(2);
            fill(col);
            translate(base.x, base.y);
            line(0, 0, vector.x, vector.y);
            rotate(vector.heading());
            let arrowSize = 7;
            translate(vector.mag() - arrowSize, 0);
            triangle(0, arrowSize / 2, 0, -arrowSize / 2, arrowSize, 0);
            pop();
        }

        function updateSourceCharge(event) {
            Q = parseFloat(event.target.value);
            document.getElementById('label-source-charge').innerText = Q.toFixed(1);
        }

        function updateTestCharge(event) {
            q = parseFloat(event.target.value);
            document.getElementById('label-test-charge').innerText = q.toFixed(4);
        }

        function updateInfoPanel(E_vec, F_vec) {
            let E_mag = E_vec.mag();
            let Ex = E_vec.x;
            let Ey = E_vec.y;
            let F_mag = F_vec.mag();

            document.getElementById('display-E-mag').innerText = `|E| = ${E_mag.toFixed(3)} N/C`;
            document.getElementById('display-Ex').innerText = `E<sub>x</sub> component = ${Ex.toFixed(3)} N/C`;
            document.getElementById('display-Ey').innerText = `E<sub>y</sub> component = ${Ey.toFixed(3)} N/C`;
            document.getElementById('display-x-pos').innerText = `x position = ${x_pos.toFixed(2)} m`;
            document.getElementById('display-y-pos').innerText = `y position = ${y_pos.toFixed(2)} m`;
            document.getElementById('display-force').innerText = `Electric force = ${F_mag.toFixed(3)} N`;
        }

        function mousePressed() {
            let mousePos = createVector(mouseX - width / 2, -(mouseY - height / 2));
            let distance = mousePos.dist(createVector(x_pos, y_pos));
            if (distance < 5) {
                dragging = true;
            }
        }

        function mouseDragged() {
            if (dragging) {
                x_pos = (mouseX - width / 2) / scale_factor;
                y_pos = -(mouseY - height / 2) / scale_factor;
            }
        }

        function mouseReleased() {
            dragging = false;
        }
    </script>
</body>
</html>
