<body>
    <h1>Fourier Transform of Radially Symmetric Potential Functions</h1>
    <div id="controls-container">
        <div id="function-selector">
            <input type="checkbox" id="checkbox-hertz" checked>
            <label for="checkbox-hertz">Hertz</label>
            <input type="checkbox" id="checkbox-overlap" checked>
            <label for="checkbox-overlap">Overlap</label>
            <input type="checkbox" id="checkbox-gaussian" checked>
            <label for="checkbox-gaussian">Gaussian</label>
        </div>
        <div id="scale-selector">
            <label>axes</label>
            <button id="btn-linear-linear" class="active">linear-linear</button>
            <button id="btn-log-linear" class="inactive">log-linear</button>
            <button id="btn-log-log" class="inactive">log-log</button>
        </div>
