            <div id="slider-r-control" class="slider-control">
                <label for="slider-r">radius r</label>
                <input type="range" id="slider-r" min="0.1" max="5" step="0.1" value="2.5">
                <span id="span-r-value">2.5</span>
            </div>
            <div id="slider-k-control" class="slider-control">
                <label for="slider-k">wave number k</label>
                <input type="range" id="slider-k" min="1" max="100" step="0.01" value="50">
                <span id="span-k-value">50</span>
            </div>
        </div>
    </div>
    <p id="hover-info">(Hover over the curves to see math labels.)</p>
    <div id="plots-container">
        <div id="plot-left"></div>
        <div id="plot-right"></div>
    </div>
    <script>
        function init() {
            const checkboxes = document.querySelectorAll('input[type="checkbox"]');
            checkboxes.forEach(checkbox => checkbox.addEventListener('change', updatePlots));

            const scaleButtons = document.querySelectorAll('#scale-selector button');
            scaleButtons.forEach(button => button.addEventListener('click', function() {
                scaleButtons.forEach(btn => btn.classList.remove('active'));
                this.classList.add('active');
                updatePlots();
            }));

            const sliders = document.querySelectorAll('input[type="range"]');
            sliders.forEach(slider => slider.addEventListener('input', updatePlots));

            updatePlots();
        }

        function updatePlots() {
            const hertzChecked = document.getElementById('checkbox-hertz').checked;
            const overlapChecked = document.getElementById('checkbox-overlap').checked;
            const gaussianChecked = document.getElementById('checkbox-gaussian').checked;

            const activeScaleButton = document.querySelector('#scale-selector button.active').id;
            const linearLinear = activeScaleButton === 'btn-linear-linear';
            const logLinear = activeScaleButton === 'btn-log-linear';
            const logLog = activeScaleButton === 'btn-log-log';

            const rValue = parseFloat(document.getElementById('slider-r').value);
            const kValue = parseFloat(document.getElementById('slider-k').value);

            document.getElementById('span-r-value').textContent = rValue.toFixed(1);
            document.getElementById('span-k-value').textContent = kValue.toFixed(2);

            const rValues = Array.from({ length: 200 }, (_, i) => (i / 199) * rValue);
            const kValues = Array.from({ length: 200 }, (_, i) => (i / 199) * kValue);

            const hertzFR = rValues.map(r => r <= 1 ? Math.pow(1 - r * r, 2.5) : 0);
            const overlapFR = rValues.map(r => r <= 1 ? Math.pow(1 - r, 2) : 0);
            const gaussianFR = rValues.map(r => Math.exp(-Math.PI * r * r));

            const hertzFK = kValues.map(k => k === 0 ? 0 : 15 * Math.PI * ((3 - k * k) * Math.sin(k) - 3 * k * Math.cos(k)) / Math.pow(k, 5));
            const overlapFK = kValues.map(k => k === 0 ? 0 : 8 * Math.PI * (2 - k * k - 2 * Math.cos(k) - 2 * k * Math.sin(k)) / Math.pow(k, 4));
            const gaussianFK = kValues.map(k => (1 / Math.sqrt(Math.PI)) * Math.exp(-k * k / (4 * Math.PI)));

            const leftPlotData = [];
            if (hertzChecked) {
                leftPlotData.push({
                    x: rValues,
                    y: hertzFR,
                    mode: 'lines',
                    name: 'Hertz',
                    line: { color: '#ff0000' },
                    hovertemplate: 'Hertz<br>f(r) = (1 - r^2)^{5/2}, r \\le 1<extra></extra>'
                });
            }
            if (overlapChecked) {
                leftPlotData.push({
                    x: rValues,
                    y: overlapFR,
                    mode: 'lines',
                    name: 'Overlap',
                    line: { color: '#000000' },
                    hovertemplate: 'Overlap<br>f(r) = (1 - r)^2, r \\le 1<extra></extra>'
                });
            }
            if (gaussianChecked) {
                leftPlotData.push({
                    x: rValues,
                    y: gaussianFR,
                    mode: 'lines',
                    name: 'Gaussian',
                    line: { color: '#0000ff' },
                    hovertemplate: 'Gaussian<br>f(r) = e^{-\\pi r^2}<extra></extra>'
                });
            }

            const rightPlotData = [];
            if (hertzChecked) {
                rightPlotData.push({
                    x: kValues,
                    y: hertzFK,
                    mode: 'lines',
                    name: 'Hertz',
                    line: { color: '#ff0000' },
                    hovertemplate: 'Hertz<br>F(k) = 15\\pi \\frac{(3-k^2)\\sin(k) - 3k\\cos(k)}{k^5}<extra></extra>'
                });
            }
            if (overlapChecked) {
                rightPlotData.push({
                    x: kValues,
                    y: overlapFK,
                    mode: 'lines',
                    name: 'Overlap',
                    line: { color: '#000000' },
                    hovertemplate: 'Overlap<br>F(k) = 8\\pi \\frac{2 - k^2 - 2\\cos(k) - 2k\\sin(k)}{k^4}<extra></extra>'
                });
            }
            if (gaussianChecked) {
                rightPlotData.push({
                    x: kValues,
                    y: gaussianFK,
                    mode: 'lines',
                    name: 'Gaussian',
                    line: { color: '#0000ff' },
                    hovertemplate: 'Gaussian<br>F(k) = \\frac{1}{\\sqrt{\\pi}} e^{-k^2 / (4\\pi)}<extra></extra>'
                });
            }

            const leftPlotLayout = {
                title: { text: 'Function', font: { color: '#aaa' } },
                xaxis: { title: 'r', range: [0, rValue] },
                yaxis: { title: 'f(r)' },
                margin: { l: 50, r: 50, b: 50, t: 50 },
                hovermode: 'closest'
            };

            const rightPlotLayout = {
                title: { text: '3D Fourier transform (real part)', font: { color: '#aaa' } },
                xaxis: { title: 'k', range: [0, kValue] },
                yaxis: { title: 'F(k)', type: linearLinear || logLinear ? 'linear' : 'log' },
                margin: { l: 50, r: 50, b: 50, t: 50 },
                hovermode: 'closest'
            };

            Plotly.react('plot-left', leftPlotData, leftPlotLayout);
            Plotly.react('plot-right', rightPlotData, rightPlotLayout);

            MathJax.typeset();
        }

        window.onload = init;
    </script>
</body>
</html>
