            annualBudget: 1.5,
            startYear: 2020,
            compensationCost: 15,
            fractionalCompensation: 50,
            constantCompensation: 25
        };

        function update() {
            // Update parameters from sliders and switches
            params.scale = document.querySelector('input[name="scale"]:checked').value;
            params.type = document.querySelector('input[name="type"]:checked').value;
            params.population = parseInt(document.getElementById('slider-population').value);
            params.annualEmissions = parseFloat(document.getElementById('slider-annual-emissions').value);
            params.reductionTime = parseInt(document.getElementById('slider-reduction-time').value);
            params.annualBudget = parseFloat(document.getElementById('slider-annual-budget').value);
            params.startYear = parseInt(document.getElementById('slider-start-year').value);
            params.compensationCost = parseFloat(document.getElementById('slider-compensation-cost').value);
            params.fractionalCompensation = parseInt(document.getElementById('slider-fractional-compensation').value);
            params.constantCompensation = parseInt(document.getElementById('slider-constant-compensation').value);

            // Update displayed values
            document.getElementById('value-population').textContent = params.population;
            document.getElementById('value-annual-emissions').textContent = params.annualEmissions.toFixed(1);
            document.getElementById('value-reduction-time').textContent = params.reductionTime;
            document.getElementById('value-annual-budget').textContent = params.annualBudget.toFixed(1);
            document.getElementById('value-start-year').textContent = params.startYear;
            document.getElementById('value-compensation-cost').textContent = params.compensationCost;
            document.getElementById('value-fractional-compensation').textContent = params.fractionalCompensation;
            document.getElementById('value-constant-compensation').textContent = params.constantCompensation;

            // Enable/disable population slider based on scale
            document.getElementById('slider-population').disabled = params.scale === 'individual';

            // Redraw the plots
            redraw();
        }

        function calculateEmissionsAndCosts() {
            const years = Array.from({ length: 31 }, (_, i) => 2020 + i);
            const realEmissions = [];
            const compensatedEmissions = [];
            const effectiveEmissions = [];
            const cumulativeEffective = [0];
            const annualCosts = [];

            for (let t of years) {
                const realEmissionPerson = t < params.startYear ? params.annualEmissions : params.annualEmissions * Math.pow(0.5, (t - params.startYear) / params.reductionTime);
                const fractionalCompensation = realEmissionPerson * (params.fractionalCompensation / 100);
                const constantCompensation = params.annualEmissions * (params.constantCompensation / 100);
                const compensatedEmissionPerson = fractionalCompensation + constantCompensation;
                const effectiveEmissionPerson = realEmissionPerson - compensatedEmissionPerson;
                const annualCost = compensatedEmissionPerson * params.compensationCost;

                realEmissions.push(realEmissionPerson);
                compensatedEmissions.push(compensatedEmissionPerson);
                effectiveEmissions.push(effectiveEmissionPerson);
                cumulativeEffective.push(cumulativeEffective[cumulativeEffective.length - 1] + effectiveEmissionPerson);
                annualCosts.push(annualCost);
            }

            return {
                years,
                realEmissions,
                compensatedEmissions,
                effectiveEmissions,
                cumulativeEffective,
                annualCosts,
                totalCosts: annualCosts.reduce((a, b) => a + b, 0)
            };
        }

