                text(years[i], xMargin + i * xScale, mainPlotHeight - yMargin + 20);
            }
            textAlign(RIGHT, CENTER);
            for (let y of [0, maxY / 4, maxY / 2, 3 * maxY / 4, maxY]) {
                text(y.toFixed(1), xMargin - 10, mainPlotHeight - yMargin - y * yScale);
            }

            // Draw curves and areas
            noFill();
            stroke(255, 0, 0);
            beginShape();
            for (let i = 0; i < years.length; i++) {
                vertex(xMargin + i * xScale, mainPlotHeight - yMargin - realEmissions[i] * yScale);
            }
            endShape();

