            }
            vertex(width - xMargin, mainPlotHeight - yMargin);
            vertex(xMargin, mainPlotHeight - yMargin);
            endShape(CLOSE);

            fill(0, 128, 0, 100);
            beginShape();
            for (let i = 0; i < years.length; i++) {
                if (effectiveEmissions[i] < 0) {
                    vertex(xMargin + i * xScale, mainPlotHeight - yMargin - effectiveEmissions[i] * yScale);
                }
