            vertex(width - xMargin, mainPlotHeight - yMargin);
            vertex(xMargin, mainPlotHeight - yMargin);
            endShape(CLOSE);

            stroke(0);
            fill(0);
            beginShape();
            for (let i = 0; i < years.length; i++) {
                vertex(xMargin + i * xScale, mainPlotHeight - yMargin - effectiveEmissions[i] * yScale);
            }
            endShape();

            // Draw climate neutral line and annotation
            if (neutralYear !== 'never') {
                const neutralIndex = neutralYear - 2020;
                stroke(0, 128, 0);
                line(xMargin + neutralIndex * xScale, mainPlotHeight - yMargin, xMargin + neutralIndex * xScale, yMargin);
                textAlign(CENTER, BOTTOM);
                text(`climate neutral ${neutralYear}`, xMargin + neutralIndex * xScale, mainPlotHeight - yMargin - 10);

                const cumulativeNeutral = cumulativeEffective[neutralIndex + 1];
                stroke(0, 128, 0, 100);
