            text(`CO₂ emissions until 2050 = ${cumulative2050.toFixed(1)} tons CO₂`, xMargin + 10, mainPlotHeight - yMargin - cumulative2050 * yScale + 10);

            // Draw budget plot
            const budgetPlotHeight = height / 2;
            fill(255);
            rect(0, mainPlotHeight, width, budgetPlotHeight);

