            const budgetValues = budgetYears.map(year => {
                const index = year - 2020;
                const totalBudget = 30 * params.annualBudget;
                return totalBudget - cumulativeEffective[index + 1];
            });

            const maxBudgetValue = Math.max(...budgetValues.map(Math.abs)) * 1.1;
            const radiusScale = 20;

            for (let i = 0; i < budgetYears.length; i++) {
                const x = (i + 1) * (width / 5);
                const y = mainPlotHeight + budgetPlotHeight / 2;
                const value = budgetValues[i];
                const radius = radiusScale * Math.sqrt(Math.abs(value));

                noStroke();
                if (value <= 0) {
                    fill(0, 128, 0);
                } else {
                    fill(255, 0, 0);
                    stroke(0);
                    strokeWeight(2);
                }
                ellipse(x, y, 2 * radius, 2 * radius);

                fill(0);
                textAlign(CENTER, CENTER);
                text(`${Math.abs(value.toFixed(1))} tons CO₂`, x, y);
            }
        }

        function redraw() {
            draw();
        }

        // Event listeners for controls
        document.querySelectorAll('input[name="scale"]').forEach(input => input.addEventListener('change', update));
        document.querySelectorAll('input[name="type"]').forEach(input => input.addEventListener('change', update));
        document.querySelectorAll('input[type="range"]').forEach(input => input.addEventListener('input', update));
        document.querySelectorAll('#climate-neutral-buttons button').forEach(button => {
            button.addEventListener('click', () => {
                document.querySelectorAll('#climate-neutral-buttons button').forEach(btn => btn.classList.remove('active'));
                button.classList.add('active');
                neutralYear = button.id === 'btn-neutral-never' ? 'never' : parseInt(button.textContent);
                update();
            });
        });

        // Initialize p5.js
        new p5();
    </script>
