        const directionalLight = new THREE.DirectionalLight(0xffffff, 0.5);
        directionalLight.position.set(1, 1, 1).normalize();
        scene.add(directionalLight);

        // Orbit Controls
        const controls = new THREE.OrbitControls(camera, renderer.domElement);
        controls.enableDamping = true;
        controls.dampingFactor = 0.25;
        controls.enableZoom = true;

        // Geometry definitions
        const solids = [
            {
                vertices: [
                    [0, 0, 0], [1, 0, 0], [0.5, Math.sqrt(3)/2, 0],
                    [-0.5, Math.sqrt(3)/2, 0], [-1, 0, 0], [-0.5, -Math.sqrt(3)/2, 0],
                    [0.5, -Math.sqrt(3)/2, 0]
                ],
                faces: [
                    [0, 1, 2], [0, 2, 3], [0, 3, 4], [0, 4, 5], [0, 5, 6], [0, 6, 1]
                ],
                hinges: [
                    { parent: 0, child: 1, axis: 'y', angle: Math.PI / 3 },
                    { parent: 0, child: 2, axis: 'y', angle: Math.PI / 3 },
                    { parent: 0, child: 3, axis: 'y', angle: Math.PI / 3 },
                    { parent: 0, child: 4, axis: 'y', angle: Math.PI / 3 },
                    { parent: 0, child: 5, axis: 'y', angle: Math.PI / 3 },
                    { parent: 0, child: 6, axis: 'y', angle: Math.PI / 3 }
                ],
                colors: ['#E47A55', '#A4C6E3', '#F4E0E1', '#F3C76E', '#B39DDB', '#FFAB91']
            },
            // Add more solids here
        ];

        let currentSolidIndex = 0;
        let currentSolid = null;
        const unfoldedColor = new THREE.Color('#F5E0E0');

        function createSolid(index) {
            const solidData = solids[index];
            const solid = new THREE.Group();

            solidData.faces.forEach((face, i) => {
                const geometry = new THREE.BufferGeometry().setFromPoints(face.map(v => new THREE.Vector3(...v)));
                const material = new THREE.MeshStandardMaterial({
                    color: solidData.colors[i],
                    side: THREE.DoubleSide,
                    flatShading: true
                });
                const mesh = new THREE.Mesh(geometry, material);

                solid.add(mesh);
            });

            solidData.hinges.forEach(hinge => {
                const pivot = new THREE.Object3D();
                const parentMesh = solid.children[hinge.parent];
                const childMesh = solid.children[hinge.child];

                // Position pivot
                const midPoint = new THREE.Vector3().addVectors(
                    new THREE.Vector3(...solidData.vertices[solidData.faces[hinge.parent][0]]),
                    new THREE.Vector3(...solidData.vertices[solidData.faces[hinge.parent][1]])
                ).divideScalar(2);
                pivot.position.copy(midPoint);

                // Orient pivot
                const axis = new THREE.Vector3();
                switch (hinge.axis) {
                    case 'x': axis.set(1, 0, 0); break;
                    case 'y': axis.set(0, 1, 0); break;
                    case 'z': axis.set(0, 0, 1); break;
                }
                pivot.up.copy(axis);

                // Position child relative to pivot
                childMesh.applyMatrix4(new THREE.Matrix4().makeTranslation(
                    -midPoint.x,
                    -midPoint.y,
                    -midPoint.z
                ));

                pivot.add(childMesh);
                parentMesh.add(pivot);
            });

            return solid;
        }

        function updateSolid(t) {
            if (!currentSolid) return;

            solids[currentSolidIndex].hinges.forEach((hinge, i) => {
                const pivot = currentSolid.children[hinge.parent].children[i];
                pivot.rotation.setFromAxisAngle(new THREE.Vector3(0, 1, 0), t * hinge.angle);
            });

            currentSolid.children.forEach((mesh, i) => {
                const material = mesh.material;
                material.color.lerpColors(unfoldedColor, new THREE.Color(solids[currentSolidIndex].colors[i]), t);
            });

            renderer.render(scene, camera);
        }

        function loadSolid(index) {
            if (currentSolid) {
                scene.remove(currentSolid);
            }

            currentSolidIndex = index;
            currentSolid = createSolid(index);
            scene.add(currentSolid);
            updateSolid(document.getElementById('slider-fold').value);
            controls.reset();
        }

        // Event listeners
        document.getElementById('btn-solid-1').addEventListener('click', () => {
            document.querySelector('.solid-btn.active').classList.remove('active');
            document.getElementById('btn-solid-1').classList.add('active');
            loadSolid(0);
        });

        document.getElementById('btn-solid-2').addEventListener('click', () => {
            document.querySelector('.solid-btn.active').classList.remove('active');
            document.getElementById('btn-solid-2').classList.add('active');
            loadSolid(1);
        });

        document.getElementById('btn-solid-3').addEventListener('click', () => {
            document.querySelector('.solid-btn.active').classList.remove('active');
            document.getElementById('btn-solid-3').classList.add('active');
            loadSolid(2);
        });

        document.getElementById('slider-fold').addEventListener('input', (event) => {
            updateSolid(event.target.value);
        });

        document.getElementById('btn-reset-view').addEventListener('click', () => {
            controls.reset();
        });

        // Initial load
        loadSolid(0);

        // Animation loop
        function animate() {
            requestAnimationFrame(animate);
            controls.update();
            renderer.render(scene, camera);
        }
        animate();

        // Resize handler
        window.addEventListener('resize', () => {
            camera.aspect = window.innerWidth * 0.8 / window.innerHeight * 0.6;
            camera.updateProjectionMatrix();
            renderer.setSize(window.innerWidth * 0.8, window.innerHeight * 0.6);
        });
    </script>
</body>
</html>
