<body>
    <div id="main-container">
        <div id="control-panel">
            <h3>Relative Volatility</h3>
            <button id="btn-reset">+</button>
            <div class="slider-row">
                <label for="slider-ac">between A and C</label>
                <input type="range" id="slider-ac" min="0.1" max="5.0" step="0.01" value="0.6">
                <span id="value-ac">0.60</span>
            </div>
            <div class="slider-row">
                <label for="slider-bc">between B and C</label>
                <input type="range" id="slider-bc" min="0.1" max="5.0" step="0.01" value="3.93">
                <span id="value-bc">3.93</span>
            </div>
        </div>
        <div id="visualization-container">
            <canvas id="ternary-plot-canvas"></canvas>
        </div>
    </div>

    <script>
        let alpha13 = 0.6;
        let alpha23 = 3.93;
        const N = 10;

        function setup() {
            createCanvas(600, 550);
            drawPlot();
        }

        function drawPlot() {
            background(249);
            noStroke();
            fill(0);

            const A = { x: 300, y: 50 };
            const B = { x: 550, y: 500 };
            const C = { x: 50, y: 500 };

            for (let i = 0; i <= N; i++) {
                for (let j = 0; j <= N - i; j++) {
                    const k = N - i - j;
                    const xA = i / N;
                    const xB = j / N;
                    const xC = k / N;

                    const px = (xA * A.x + xB * B.x + xC * C.x);
                    const py = (xA * A.y + xB * B.y + xC * C.y);

                    const D = alpha13 * xA + alpha23 * xB + xC;
                    const yA = (alpha13 * xA) / D;
                    const yB = (alpha23 * xB) / D;
                    const yC = xC / D;

                    const F = pow(xA - yA, 2) + pow(xB - yB, 2) + pow(xC - yC, 2);
                    const v = F / 2.0;

                    const color = lerpColor(
                        color('#2c7bb6'),
                        color('#d7191c'),
                        v
                    );
                    fill(color);

                    const p1 = { x: px, y: py };
                    const p2 = { x: (i + 1) / N * A.x + j / N * B.x + k / N * C.x, y: (i + 1) / N * A.y + j / N * B.y + k / N * C.y };
                    const p3 = { x: i / N * A.x + (j + 1) / N * B.x + k / N * C.x, y: i / N * A.y + (j + 1) / N * B.y + k / N * C.y };

                    beginShape();
                    vertex(p1.x, p1.y);
                    vertex(p2.x, p2.y);
                    vertex(p3.x, p3.y);
                    endShape(CLOSE);

                    stroke(0);
                    strokeWeight(0.5);
                    line(p1.x, p1.y, p2.x, p2.y);
                    line(p2.x, p2.y, p3.x, p3.y);
                    line(p3.x, p3.y, p1.x, p1.y);
                    noStroke();
                }
            }

            stroke(0);
            strokeWeight(2);
            line(A.x, A.y, B.x, B.y);
            line(B.x, B.y, C.x, C.y);
            line(C.x, C.y, A.x, A.y);
            noStroke();

            textSize(20);
            textAlign(CENTER, CENTER);
            fill(0);
            text('A', A.x, A.y - 20);
            text('B', B.x + 10, B.y + 10);
            text('C', C.x - 10, C.y + 10);

            for (let i = 0; i <= N; i++) {
                const t = i / N;
                const Ax = lerp(A.x, B.x, t);
                const Ay = lerp(A.y, B.y, t);
                const Bx = lerp(B.x, C.x, t);
                const By = lerp(B.y, C.y, t);
                const Cx = lerp(C.x, A.x, t);
                const Cy = lerp(C.y, A.y, t);

                textSize(12);
                textAlign(CENTER, CENTER);
                fill(0);
                text(t.toFixed(1), Ax, Ay - 10);
                text(t.toFixed(1), Bx + 5, By + 5);
                text(t.toFixed(1), Cx - 5, Cy + 5);
            }
        }

        function updateValues() {
            alpha13 = parseFloat(document.getElementById('slider-ac').value);
            alpha23 = parseFloat(document.getElementById('slider-bc').value);
            document.getElementById('value-ac').textContent = alpha13.toFixed(2);
            document.getElementById('value-bc').textContent = alpha23.toFixed(2);
            drawPlot();
        }

        document.getElementById('slider-ac').addEventListener('input', updateValues);
        document.getElementById('slider-bc').addEventListener('input', updateValues);
        document.getElementById('btn-reset').addEventListener('click', () => {
            document.getElementById('slider-ac').value = 0.6;
            document.getElementById('slider-bc').value = 3.93;
            updateValues();
        });

        updateValues();
    </script>
</body>
</html>
