</head>
<body>
    <h1>Band Structure of a Quantum Wire with Rashba and Zeeman Interactions</h1>
    <div id="main-container">
        <div id="control-panel">
            <label for="slider-wso">Rashba spin-orbit interaction strength (wso)</label>
            <input type="range" id="slider-wso" min="0" max="2" step="0.1" value="0">
            <span id="value-wso">0.0</span>

            <hr>

            <h2>magnetic field</h2>
            <label for="slider-n">subband (n)</label>
            <input type="range" id="slider-n" min="0" max="10" step="1" value="0">
            <span id="value-n">0</span>

            <h3>strengths</h3>
            <label for="slider-bx">x direction (BX)</label>
            <input type="range" id="slider-bx" min="0" max="2" step="0.1" value="0">
            <span id="value-bx">0.0</span>

            <label for="slider-by">y direction (BY)</label>
            <input type="range" id="slider-by" min="0" max="2" step="0.1" value="0">
            <span id="value-by">0.0</span>

            <label for="slider-bz">z direction (BZ)</label>
            <input type="range" id="slider-bz" min="0" max="2" step="0.1" value="0">
            <span id="value-bz">0.0</span>
        </div>
        <div id="plot-container"></div>
    </div>

    <script>
        const sliderWso = document.getElementById('slider-wso');
        const sliderN = document.getElementById('slider-n');
        const sliderBx = document.getElementById('slider-bx');
        const sliderBy = document.getElementById('slider-by');
        const sliderBz = document.getElementById('slider-bz');

        const valueWso = document.getElementById('value-wso');
        const valueN = document.getElementById('value-n');
        const valueBx = document.getElementById('value-bx');
        const valueBy = document.getElementById('value-by');
        const valueBz = document.getElementById('value-bz');

        sliderWso.addEventListener('input', updateValues);
        sliderN.addEventListener('input', updateValues);
        sliderBx.addEventListener('input', updateValues);
        sliderBy.addEventListener('input', updateValues);
        sliderBz.addEventListener('input', updateValues);

        function updateValues() {
            valueWso.textContent = parseFloat(sliderWso.value).toFixed(1);
            valueN.textContent = parseInt(sliderN.value);
            valueBx.textContent = parseFloat(sliderBx.value).toFixed(1);
            valueBy.textContent = parseFloat(sliderBy.value).toFixed(1);
            valueBz.textContent = parseFloat(sliderBz.value).toFixed(1);

            updatePlot();
        }

        function updatePlot() {
            const n = parseInt(sliderN.value);
            const wso = parseFloat(sliderWso.value);
            const BX = parseFloat(sliderBx.value);
            const BY = parseFloat(sliderBy.value);
            const BZ = parseFloat(sliderBz.value);

            const kValues = [];
            const EPlusValues = [];
            const EMinusValues = [];

            for (let k = -5; k <= 5; k += 0.1) {
                kValues.push(k);
                const termInsideSqrt = Math.sqrt(Math.pow(BZ + wso * k, 2) + Math.pow(BX, 2) + Math.pow(BY, 2));
                const EPlus = (2 * n + 1) + Math.pow(k, 2) + termInsideSqrt;
                const EMinus = (2 * n + 1) + Math.pow(k, 2) - termInsideSqrt;
                EPlusValues.push(EPlus);
                EMinusValues.push(EMinus);
            }

            const traces = [];
            let titleParams = `n = ${n}`;

            if (wso !== 0 || BX !== 0 || BY !== 0 || BZ !== 0) {
                if (wso !== 0) titleParams += `, wso = ${wso.toFixed(1)}`;
                if (BX !== 0) titleParams += `, BX = ${BX.toFixed(1)}`;
                if (BY !== 0) titleParams += `, BY = ${BY.toFixed(1)}`;
                if (BZ !== 0) titleParams += `, BZ = ${BZ.toFixed(1)}`;

                traces.push({
                    x: kValues,
                    y: EPlusValues,
                    mode: 'lines',
                    line: { color: 'red', dash: 'solid', width: 2 },
                    name: 'E_plus'
                });

                traces.push({
                    x: kValues,
                    y: EMinusValues,
                    mode: 'lines',
                    line: { color: 'red', dash: 'dash', width: 2 },
                    name: 'E_minus'
                });
            } else {
                traces.push({
                    x: kValues,
                    y: EPlusValues,
                    mode: 'lines',
                    line: { color: 'red', dash: 'solid', width: 2 },
                    name: 'Degenerate State'
                });
            }

            const layout = {
                title: titleParams,
                xaxis: {
                    title: 'normalized k length (lwky)',
                    range: [-5, 5],
                    zeroline: true
                },
                yaxis: {
                    title: '2 E / &hbar;&omega;',
                    range: [0, 40],
                    zeroline: true
                }
            };

            Plotly.newPlot('plot-container', traces, layout);
        }

        // Initialize plot on page load
        updatePlot();
    </script>
</body>
</html>
