            new p5((p) => {
                p.setup = () => {
                    p.createCanvas(canvasWidth, canvasHeight);
                    p.parent('plot-canvas-4');
                    update();
                };
                p.draw = () => {
                    drawPlot(p, C2, 't (hr)', 'Q (cfs)', 8, 100, 1);
                };
            });

            new p5((p) => {
                p.setup = () => {
                    p.createCanvas(canvasWidth, canvasHeight);
                    p.parent('plot-canvas-5');
                    update();
                };
                p.draw = () => {
                    drawPlot(p, C3, 't (hr)', 'Q (cfs)', 8, 100, 2);
                };
            });

            new p5((p) => {
                p.setup = () => {
                    p.createCanvas(canvasWidth, canvasHeight);
                    p.parent('plot-canvas-6');
                    update();
                };
                p.draw = () => {
                    drawPlot(p, Q, 't (hr)', 'Q (cfs)', 8, 100, 0);
                };
            });

            document.querySelectorAll('input[type="range"]').forEach(slider => {
                slider.addEventListener('input', update);
            });

            document.getElementById('toggle-controls').addEventListener('click', () => {
                const slidersArea = document.getElementById('sliders-area');
                if (slidersArea.style.display === 'none' || slidersArea.style.display === '') {
                    slidersArea.style.display = 'block';
                    document.getElementById('toggle-controls').textContent = '-';
                } else {
                    slidersArea.style.display = 'none';
                    document.getElementById('toggle-controls').textContent = '+';
                }
            });
        }

        function update() {
            P = [
                parseInt(document.getElementById('slider-precip-1').value),
                parseInt(document.getElementById('slider-precip-2').value),
                parseInt(document.getElementById('slider-precip-3').value)
            ];
            U = [
                parseInt(document.getElementById('slider-uh-1').value),
                parseInt(document.getElementById('slider-uh-2').value),
                parseInt(document.getElementById('slider-uh-3').value),
                parseInt(document.getElementById('slider-uh-4').value)
            ];

            document.getElementById('value-precip-1').textContent = P[0];
            document.getElementById('value-precip-2').textContent = P[1];
            document.getElementById('value-precip-3').textContent = P[2];
            document.getElementById('value-uh-1').textContent = U[0];
            document.getElementById('value-uh-2').textContent = U[1];
            document.getElementById('value-uh-3').textContent = U[2];
            document.getElementById('value-uh-4').textContent = U[3];

            C1 = P[0] ? U.map(u => u * P[0]) : [0, 0, 0, 0];
            C2 = P[1] ? U.map(u => u * P[1]) : [0, 0, 0, 0];
            C3 = P[2] ? U.map(u => u * P[2]) : [0, 0, 0, 0];

            Q = [
                C1[0],
                C1[1] + C2[0],
                C1[2] + C2[1] + C3[0],
                C1[3] + C2[2] + C3[1],
                C2[3] + C3[2],
                C3[3]
            ];
        }

        function drawPlot(p, data, xLabel, yLabel, xMax, yMax, xOffset = 0) {
            p.background(255);
            p.strokeWeight(2);

            // Draw axes
            p.stroke(0);
            p.line(50, 50, 50, p.height - 50);
            p.line(50, p.height - 50, p.width - 50, p.height - 50);

            // Draw x-axis ticks and labels
            for (let x = 0; x <= xMax; x += 1) {
                const xPos = map(x + xOffset, 0, xMax, 50, p.width - 50);
                p.line(xPos, p.height - 50, xPos, p.height - 45);
                p.textAlign(p.CENTER, p.TOP);
                p.text(x, xPos, p.height - 40);
            }

            // Draw y-axis ticks and labels
            for (let y = 0; y <= yMax; y += 2) {
                const yPos = map(y, 0, yMax, p.height - 50, 50);
                p.line(50, yPos, 55, yPos);
                p.textAlign(p.RIGHT, p.CENTER);
                p.text(y, 45, yPos);
            }

            // Draw labels
            p.textAlign(p.CENTER, p.BOTTOM);
            p.text(xLabel, p.width / 2, p.height - 20);
            p.translate(20, p.height / 2);
            p.rotate(-Math.PI / 2);
            p.text(yLabel, 0, 0);
            p.resetMatrix();

            // Draw step chart
            p.fill('#d4e6f1');
            p.stroke('#2e86c1');

            for (let i = 0; i < data.length; i++) {
                const x1 = map(i + xOffset, 0, xMax, 50, p.width - 50);
                const y1 = map(data[i], 0, yMax, p.height - 50, 50);
                const x2 = map(i + 1 + xOffset, 0, xMax, 50, p.width - 50);
                const y2 = map(data[i], 0, yMax, p.height - 50, 50);

                p.line(x1, y1, x2, y2);
                if (i > 0) {
                    p.beginShape();
                    p.vertex(x1, y1);
                    p.vertex(x1, map(data[i - 1], 0, yMax, p.height - 50, 50));
                    p.vertex(x2, map(data[i - 1], 0, yMax, p.height - 50, 50));
                    p.vertex(x2, y2);
                    p.endShape(p.CLOSE);
                }
            }
        }
    </script>
</body>
</html>
