                } else if (x1_indiff >= 0 && x1_indiff <= 1) {
                    p.line(x1_indiff, 0, x1_indiff, 1);
                    if (den2 > 0) {
                        p.line(0, 0, x1_indiff, 0);
                        p.line(x1_indiff, 1, 1, 1);
                    } else {
