                        p.line(x1_indiff, 0, 1, 0);
                    }
                } else {
                    let y1_pref = (b11 * 0.5 + b21 * (1 - 0.5)) - (b12 * 0.5 + b22 * (1 - 0.5));
                    if (y1_pref > 0) p.line(0, 1, 1, 1);
                    else p.line(0, 0, 1, 0);
                }

