                if (Math.abs(den1) < 1e-6) {
                    if (a12 - a22 > 0) {
                        if (Math.abs(x1_indiff - 1) < 1e-6) eqPoints.push([1, 1]);
                    } else {
                        if (Math.abs(x1_indiff) < 1e-6) eqPoints.push([0, 0]);
                    }
                } else if (y1_indiff >= 0 && y1_indiff <= 1) {
                    if (Math.abs(den2) < 1e-6) {
                        if (b12 - b22 > 0) {
                            if (Math.abs(y1_indiff - 1) < 1e-6) eqPoints.push([1, 1]);
                        } else {
                            if (Math.abs(y1_indiff) < 1e-6) eqPoints.push([0, 0]);
                        }
                    } else if (x1_indiff >= 0 && x1_indiff <= 1) {
                        eqPoints.push([x1_indiff, y1_indiff]);
                    }
                }
                eqPoints.forEach(point => {
                    p.fill(255, 0, 0);
                    p.circle(point[0], point[1], 0.1);
                });

                // Update Nash Equilibria Display
                let eqStr = eqPoints.map(point => `(${toFraction(point[0])}, ${toFraction(point[1])})`).join(', ');
                document.getElementById('equilibria-display').innerHTML = `\\( \\{ ${eqStr} \\} \\)`;
                MathJax.typeset();
            };
        };

        new p5(sketch, 'canvas-container');

        updateValues();
    </script>
