            [75, 0, 130, 100],
            [238, 130, 238, 100]
        ];

        function setup() {
            createCanvas(600, 500);
            angleMode(RADIANS);
            frameRate(30);

            document.getElementById('slider-t').addEventListener('input', updateT);
            document.getElementById('input-t').addEventListener('input', updateT);
            document.getElementById('checkbox-family').addEventListener('change', toggleFamily);
            document.getElementById('btn-reset').addEventListener('click', resetDemo);
        }

        function draw() {
            background(255);
            drawAxes();
