        function drawAxes() {
            stroke(200);
            strokeWeight(1);
            const scale = 50;
            const midX = width / 2;
            const midY = height / 2;

            line(0, midY, width, midY); // X-axis
            line(midX, 0, midX, height); // Y-axis

