        }

        function drawQuadraticCurve(coeffs, color) {
            stroke(color);
            strokeWeight(3);
            beginShape();
            for (let x = -6; x <= 6; x += 0.01) {
                const y = coeffs[0] * pow(x, 2) + coeffs[1] * x + coeffs[2];
                vertex(map(x, -6, 6, 0, width), map(y, -5, 5, height, 0));
            }
            endShape();
        }

        function drawFamilyOfQuadratics() {
            const step = 0.5;
            for (let tVal = -5; tVal <= 5; tVal += step) {
                const coeffs = getOsculatingQuadraticCoeffs(tVal, cubicCoeffs);
                const colorIndex = Math.floor((tVal + 5) / step) % colorPalette.length;
                const color = colorPalette[colorIndex];
                drawFilledQuadratic(coeffs, color);
            }
            drawQuadraticCurve(quadraticCoeffs, '#f57c00');
        }

        function drawFilledQuadratic(coeffs, color) {
            const vertices = [];
            for (let x = -6; x <= 6; x += 0.01) {
                const y = coeffs[0] * pow(x, 2) + coeffs[1] * x + coeffs[2];
                vertices.push({ x: map(x, -6, 6, 0, width), y: map(y, -5, 5, height, 0) });
            }
            const boundaryY = coeffs[0] > 0 ? height : 0;
            fill(...color);
            noStroke();
            beginShape();
            vertex(vertices[0].x, boundaryY);
            vertices.forEach(v => vertex(v.x, v.y));
            vertex(vertices[vertices.length - 1].x, boundaryY);
            endShape();
        }

        function drawLocators() {
            const scale = 50;
            const midX = width / 2;
            const midY = height / 2;

            for (let i = 0; i < locators.length; i++) {
                const { x, y } = locators[i];
                const px = midX + x * scale;
                const py = midY - y * scale;

                fill('white');
                stroke('black');
                strokeWeight(2);
                ellipse(px, py, 10, 10);

                strokeWeight(1);
                line(px - 5, py, px + 5, py);
                line(px, py - 5, px, py + 5);
            }
        }

        function mousePressed() {
            for (let i = 0; i < locators.length; i++) {
                const { x, y } = locators[i];
                const px = map(x, -6, 6, 0, width);
                const py = map(y, -5, 5, height, 0);
                const d = dist(mouseX, mouseY, px, py);
                if (d < 5) {
                    locators[i].dragging = true;
                    break;
                }
            }
        }

        function mouseDragged() {
            for (let i = 0; i < locators.length; i++) {
                if (locators[i].dragging) {
                    locators[i].x = map(mouseX, 0, width, -6, 6);
                    locators[i].y = map(mouseY, 0, height, 5, -5);
                    cubicCoeffs = solveCubicCoefficients(locators);
                    quadraticCoeffs = getOsculatingQuadraticCoeffs(t, cubicCoeffs);
                    break;
                }
            }
        }

        function mouseReleased() {
            for (let i = 0; i < locators.length; i++) {
                locators[i].dragging = false;
            }
        }

        function updateT(event) {
            t = parseFloat(event.target.value);
            document.getElementById('slider-t').value = t;
            document.getElementById('input-t').value = t;
            quadraticCoeffs = getOsculatingQuadraticCoeffs(t, cubicCoeffs);
        }

        function toggleFamily(event) {
            familyChecked = event.target.checked;
        }

        function resetDemo() {
            locators = [
                { x: -4, y: 1 },
                { x: -2, y: 1.8 },
                { x: 1, y: -1 },
                { x: 4, y: -1 }
            ];
            t = 0.5;
            document.getElementById('slider-t').value = t;
            document.getElementById('input-t').value = t;
            document.getElementById('checkbox-family').checked = false;
            familyChecked = false;
            cubicCoeffs = solveCubicCoefficients(locators);
            quadraticCoeffs = getOsculatingQuadraticCoeffs(t, cubicCoeffs);
        }

        function solveCubicCoefficients(points) {
            const A = [
                [pow(points[0].x, 3), pow(points[0].x, 2), points[0].x, 1],
                [pow(points[1].x, 3), pow(points[1].x, 2), points[1].x, 1],
                [pow(points[2].x, 3), pow(points[2].x, 2), points[2].x, 1],
                [pow(points[3].x, 3), pow(points[3].x, 2), points[3].x, 1]
            ];
            const B = [points[0].y, points[1].y, points[2].y, points[3].y];
            const detA = determinant(A);
            if (detA === 0) throw new Error("Singular matrix");
            const a = determinant([
                [B[0], A[0][1], A[0][2], A[0][3]],
                [B[1], A[1][1], A[1][2], A[1][3]],
                [B[2], A[2][1], A[2][2], A[2][3]],
                [B[3], A[3][1], A[3][2], A[3][3]]
            ]) / detA;
            const b = determinant([
                [A[0][0], B[0], A[0][2], A[0][3]],
                [A[1][0], B[1], A[1][2], A[1][3]],
                [A[2][0], B[2], A[2][2], A[2][3]],
                [A[3][0], B[3], A[3][2], A[3][3]]
            ]) / detA;
            const c = determinant([
                [A[0][0], A[0][1], B[0], A[0][3]],
                [A[1][0], A[1][1], B[1], A[1][3]],
                [A[2][0], A[2][1], B[2], A[2][3]],
                [A[3][0], A[3][1], B[3], A[3][3]]
            ]) / detA;
            const d = determinant([
                [A[0][0], A[0][1], A[0][2], B[0]],
                [A[1][0], A[1][1], A[1][2], B[1]],
                [A[2][0], A[2][1], A[2][2], B[2]],
                [A[3][0], A[3][1], A[3][2], B[3]]
            ]) / detA;
            return [a, b, c, d];
        }

        function determinant(matrix) {
            if (matrix.length === 2) {
                return matrix[0][0] * matrix[1][1] - matrix[0][1] * matrix[1][0];
            }
            let det = 0;
            for (let i = 0; i < matrix.length; i++) {
                det += Math.pow(-1, i) * matrix[0][i] * determinant(minor(matrix, 0, i));
            }
            return det;
        }

        function minor(matrix, row, col) {
            return matrix.slice(0, row).concat(matrix.slice(row + 1)).map(subRow => subRow.slice(0, col).concat(subRow.slice(col + 1)));
        }

        function getOsculatingQuadraticCoeffs(t, cubicCoeffs) {
            const a = cubicCoeffs[0];
            const b = cubicCoeffs[1];
            const c = cubicCoeffs[2];
            const d = cubicCoeffs[3];
            const p = 3 * a * t + b;
            const q = -3 * a * pow(t, 2) + c;
            const r = a * pow(t, 3) + d;
            return [p, q, r];
        }
    </script>
</body>
