                T_max = 1000;
                document.getElementById('slider-T').min = T_min;
                document.getElementById('slider-T').max = T_max;
                document.getElementById('slider-T').step = 1;
            }

            for (let t = T_min; t <= T_max; t += 1) {
                let Tk = t;
                if (T_unit === 'C') Tk += T0;
                T_values.push(t);
                V_values.push(k * Tk);
