                yaxis: {title: 'volume', range: [0, 2000]},
                showlegend: false
            };

            Plotly.newPlot('plot-container', [trace1, trace2], layout);
        }

        function updatePiston() {
            let currentTk = T;
            if (T_unit === 'C') currentTk += T0;
            let currentV = k * currentTk;

            let colorLow = color(128, 0, 128); // Deep purple
            let colorMid = color(255, 255, 0); // Yellow
            let colorHigh = color(255, 0, 0); // Red
            let currentColor = lerpColor(colorLow, colorHigh, currentTk / 1000);

            function setup() {
                createCanvas(400, 400);
            }

            function draw() {
                background(220);
                fill(0, 0, 255);
                rect(100, 0, 20, 400); // Left wall
                rect(300, 0, 20, 400); // Right wall
                line(100, 400, 300, 400); // Base

                let pistonHeight = map(currentV, 0, 2000, 0, 400);
                fill(169, 169, 169);
                rect(100, 400 - pistonHeight, 200, 20); // Piston plate
                rect(200, 400 - pistonHeight, 20, pistonHeight); // Piston block

                fill(currentColor);
                rect(100, 400 - pistonHeight, 200, pistonHeight); // Gas

                fill(255, 165, 0);
                triangle(150, 400 - pistonHeight + 20, 160, 400 - pistonHeight + 10, 170, 400 - pistonHeight + 20); // Arrow 1
                triangle(230, 400 - pistonHeight + 20, 240, 400 - pistonHeight + 10, 250, 400 - pistonHeight + 20); // Arrow 2
            }

            new p5((p) => {
                p.setup = setup;
                p.draw = draw;
            }, 'piston-container');
        }

        document.getElementById('slider-v0').addEventListener('input', () => {
            v0 = parseFloat(document.getElementById('slider-v0').value);
            k = v0 / T0;
            updatePlot();
            updatePiston();
        });

        document.getElementById('slider-T').addEventListener('input', () => {
            T = parseFloat(document.getElementById('slider-T').value);
            updatePlot();
            updatePiston();
        });

        document.querySelectorAll('input[name="temp-units"]').forEach(radio => {
            radio.addEventListener('change', () => {
                T_unit = radio.value;
                if (T_unit === 'K') {
                    T = T + T0;
                } else {
                    T = T - T0;
                }
                document.getElementById('slider-T').value = T;
                updatePlot();
                updatePiston();
            });
        });

        updatePlot();
        updatePiston();
    </script>
