    <script>
        let angleA = 306;
        let angleB = 54;
        let angleC = 180;
        let selectedVertex = 'C';
        let draggingVertex = null;

        function setup() {
            const canvasSize = 600;
            createCanvas(canvasSize, canvasSize).parent('canvas-container');
            const sliders = document.querySelectorAll('input[type="range"]');
            sliders.forEach(slider => slider.addEventListener('input', updateAngles));
            const radioButtons = document.querySelectorAll('input[name="vertex-select"]');
            radioButtons.forEach(radio => radio.addEventListener('change', updateSelectedVertex));
        }

        function draw() {
            background(255);
            const centerX = width / 2;
            const centerY = height / 2;
            const radius = 250;

            // Draw background sectors
            drawSector(centerX, centerY, radius, angleA, angleB, '#e0f0e0'); // Arc AB
            drawSector(centerX, centerY, radius, angleB, angleC, '#f5f5d5'); // Arc BC
            drawSector(centerX, centerY, radius, angleC, angleA, '#e0e8f8'); // Arc CA

            // Draw the circle
            noFill();
            stroke('#666666');
            strokeWeight(2);
            circle(centerX, centerY, radius * 2);

            // Draw the triangle
            fill('rgba(173, 216, 230, 0.5)');
            stroke('#666666');
            beginShape();
            vertex(centerX + radius * cos(radians(angleA)), centerY + radius * sin(radians(angleA)));
            vertex(centerX + radius * cos(radians(angleB)), centerY + radius * sin(radians(angleB)));
            vertex(centerX + radius * cos(radians(angleC)), centerY + radius * sin(radians(angleC)));
            endShape(CLOSE);

            // Draw vertex labels
            fill('#00008B');
            textSize(20);
            textStyle(BOLD);
            text('A', centerX + radius * cos(radians(angleA)) + 10, centerY + radius * sin(radians(angleA)) + 10);
            text('B', centerX + radius * cos(radians(angleB)) + 10, centerY + radius * sin(radians(angleB)) + 10);
            text('C', centerX + radius * cos(radians(angleC)) + 10, centerY + radius * sin(radians(angleC)) + 10);

            // Draw central and inscribed angles if a vertex is selected
            if (selectedVertex !== 'None') {
                let arcStart, arcEnd, centralAngle, inscribedAngle;
                switch (selectedVertex) {
                    case 'A':
                        arcStart = angleB;
                        arcEnd = angleC;
                        break;
                    case 'B':
                        arcStart = angleC;
                        arcEnd = angleA;
                        break;
                    case 'C':
                        arcStart = angleA;
                        arcEnd = angleB;
                        break;
                }
                centralAngle = (arcEnd - arcStart + 360) % 360;
                inscribedAngle = centralAngle / 2;

                // Draw central angle
                stroke('#008000');
                strokeWeight(4);
                arc(centerX, centerY, radius * 2, radius * 2, radians(arcStart), radians(arcEnd), PIE);
                drawArrowhead(centerX, centerY, radius, arcStart, arcEnd, '#008000');

                // Draw inscribed angle
                stroke('#FF0000');
                strokeWeight(4);
                arc(centerX + radius * cos(radians(selectedVertex === 'A' ? angleA : selectedVertex === 'B' ? angleB : angleC)),
                    centerY + radius * sin(radians(selectedVertex === 'A' ? angleA : selectedVertex === 'B' ? angleB : angleC)),
                    50, 50, radians(arcStart), radians(arcEnd), PIE);
                drawArrowhead(centerX + radius * cos(radians(selectedVertex === 'A' ? angleA : selectedVertex === 'B' ? angleB : angleC)),
                    centerY + radius * sin(radians(selectedVertex === 'A' ? angleA : selectedVertex === 'B' ? angleB : angleC)),
                    25, arcStart, arcEnd, '#FF0000');

                // Draw labels
                fill('#008000');
                textSize(16);
                textAlign(CENTER, CENTER);
                text(`θ = ${centralAngle.toFixed(1)}°`, centerX + radius * 0.5 * cos(radians((arcStart + arcEnd) / 2)),
                    centerY + radius * 0.5 * sin(radians((arcStart + arcEnd) / 2)));

                fill('#FF0000');
                text(`θ/2 = ${inscribedAngle.toFixed(1)}°`, centerX + radius * 0.25 * cos(radians((arcStart + arcEnd) / 2)) + radius * cos(radians(selectedVertex === 'A' ? angleA : selectedVertex === 'B' ? angleB : angleC)),
                    centerY + radius * 0.25 * sin(radians((arcStart + arcEnd) / 2)) + radius * sin(radians(selectedVertex === 'A' ? angleA : selectedVertex === 'B' ? angleB : angleC)));
            }

            // Draw center dot
            fill('#666666');
            noStroke();
            circle(centerX, centerY, 5);

            // Update info panel
            updateInfoPanel();
        }

        function drawSector(centerX, centerY, radius, startAngle, endAngle, color) {
            fill(color);
            noStroke();
            arc(centerX, centerY, radius * 2, radius * 2, radians(startAngle), radians(endAngle), PIE);
        }

        function drawArrowhead(x, y, radius, startAngle, endAngle, color) {
            const midAngle = (startAngle + endAngle) / 2;
            const tipX = x + radius * cos(radians(midAngle));
            const tipY = y + radius * sin(radians(midAngle));
            const leftX = x + (radius - 10) * cos(radians(midAngle - 15));
            const leftY = y + (radius - 10) * sin(radians(midAngle - 15));
            const rightX = x + (radius - 10) * cos(radians(midAngle + 15));
            const rightY = y + (radius - 10) * sin(radians(midAngle + 15));

            fill(color);
            noStroke();
            triangle(tipX, tipY, leftX, leftY, rightX, rightY);
        }

        function updateAngles() {
            angleA = parseFloat(document.getElementById('slider-a').value);
            angleB = parseFloat(document.getElementById('slider-b').value);
            angleC = parseFloat(document.getElementById('slider-c').value);
            updateSliders();
        }

        function updateSelectedVertex() {
            selectedVertex = document.querySelector('input[name="vertex-select"]:checked').value;
        }

        function updateSliders() {
            document.getElementById('slider-a').value = angleA;
            document.getElementById('slider-b').value = angleB;
            document.getElementById('slider-c').value = angleC;
        }

        function updateInfoPanel() {
            const arcAB = (angleB - angleA + 360) % 360;
            const arcBC = (angleC - angleB + 360) % 360;
            const arcCA = (angleA - angleC + 360) % 360;

            const vertexA_angle = arcBC / 2;
            const vertexB_angle = arcCA / 2;
            const vertexC_angle = arcAB / 2;

            if (selectedVertex === 'None') {
                document.getElementById('arc-label-1').innerText = 'Arc AB';
                document.getElementById('arc-value-1').innerText = `${arcAB.toFixed(1)}°`;
                document.getElementById('vertex-label-1').innerText = 'Vertex C';
                document.getElementById('vertex-value-1').innerText = `${vertexC_angle.toFixed(1)}°`;

                document.getElementById('arc-label-2').innerText = 'Arc BC';
                document.getElementById('arc-value-2').innerText = `${arcBC.toFixed(1)}°`;
                document.getElementById('vertex-label-2').innerText = 'Vertex A';
                document.getElementById('vertex-value-2').innerText = `${vertexA_angle.toFixed(1)}°`;

                document.getElementById('arc-label-3').innerText = 'Arc CA';
                document.getElementById('arc-value-3').innerText = `${arcCA.toFixed(1)}°`;
                document.getElementById('vertex-label-3').innerText = 'Vertex B';
                document.getElementById('vertex-value-3').innerText = `${vertexB_angle.toFixed(1)}°`;

                document.getElementById('arc-total').innerText = '360°';
                document.getElementById('vertex-total').innerText = '180°';
            } else {
                switch (selectedVertex) {
                    case 'A':
                        document.getElementById('arc-label-1').innerText = 'Arc BC';
                        document.getElementById('arc-value-1').innerText = `${arcBC.toFixed(1)}°`;
                        document.getElementById('vertex-label-1').innerText = 'Vertex A';
                        document.getElementById('vertex-value-1').innerText = `${vertexA_angle.toFixed(1)}°`;
                        break;
                    case 'B':
                        document.getElementById('arc-label-1').innerText = 'Arc CA';
                        document.getElementById('arc-value-1').innerText = `${arcCA.toFixed(1)}°`;
                        document.getElementById('vertex-label-1').innerText = 'Vertex B';
                        document.getElementById('vertex-value-1').innerText = `${vertexB_angle.toFixed(1)}°`;
                        break;
                    case 'C':
                        document.getElementById('arc-label-1').innerText = 'Arc AB';
                        document.getElementById('arc-value-1').innerText = `${arcAB.toFixed(1)}°`;
                        document.getElementById('vertex-label-1').innerText = 'Vertex C';
                        document.getElementById('vertex-value-1').innerText = `${vertexC_angle.toFixed(1)}°`;
                        break;
                }
                document.getElementById('arc-label-2').innerText = '';
                document.getElementById('arc-value-2').innerText = '';
                document.getElementById('vertex-label-2').innerText = '';
                document.getElementById('vertex-value-2').innerText = '';

                document.getElementById('arc-label-3').innerText = '';
                document.getElementById('arc-value-3').innerText = '';
                document.getElementById('vertex-label-3').innerText = '';
                document.getElementById('vertex-value-3').innerText = '';

                document.getElementById('arc-total').innerText = '?';
                document.getElementById('vertex-total').innerText = '?';
            }
        }

        function mousePressed() {
            const centerX = width / 2;
            const centerY = height / 2;
            const radius = 250;
            const threshold = 15;

            if (dist(mouseX, mouseY, centerX + radius * cos(radians(angleA)), centerY + radius * sin(radians(angleA))) < threshold) {
                draggingVertex = 'A';
            } else if (dist(mouseX, mouseY, centerX + radius * cos(radians(angleB)), centerY + radius * sin(radians(angleB))) < threshold) {
                draggingVertex = 'B';
            } else if (dist(mouseX, mouseY, centerX + radius * cos(radians(angleC)), centerY + radius * sin(radians(angleC))) < threshold) {
                draggingVertex = 'C';
            }
        }

        function mouseDragged() {
            if (draggingVertex) {
                const centerX = width / 2;
                const centerY = height / 2;
                const angle = degrees(atan2(mouseY - centerY, mouseX - centerX));
                let newAngle = (angle + 360) % 360;

                switch (draggingVertex) {
                    case 'A':
                        angleA = newAngle;
                        document.getElementById('slider-a').value = angleA;
                        break;
                    case 'B':
                        angleB = newAngle;
                        document.getElementById('slider-b').value = angleB;
                        break;
                    case 'C':
                        angleC = newAngle;
                        document.getElementById('slider-c').value = angleC;
                        break;
                }
            }
        }

        function mouseReleased() {
            draggingVertex = null;
        }
    </script>
</body>
