        const N = 100; // Number of grid points
        const xi = Array.from({ length: N }, (_, i) => i / (N - 1)); // Spatial grid

        let tau = 1.0;
        let phi = 2.0;
        let beta = 0.2;
        let gamma = 20.0;
        let xi_marker = 0.4;

        function rk4_step(f, y, t, h) {
            const k1 = f(y, t);
            const k2 = f([y[0] + h * k1[0] / 2, y[1] + h * k1[1] / 2], t + h / 2);
            const k3 = f([y[0] + h * k2[0] / 2, y[1] + h * k2[1] / 2], t + h / 2);
            const k4 = f([y[0] + h * k3[0], y[1] + h * k3[1]], t + h);
            return [y[0] + h * (k1[0] + 2 * k2[0] + 2 * k3[0] + k4[0]) / 6, y[1] + h * (k1[1] + 2 * k2[1] + 2 * k3[1] + k4[1]) / 6];
        }

        function rhs([c, theta], xi) {
            const dc_dxi = (c.slice(1) - 2 * c.slice(0, -1) + c.slice(0, -2)) / (xi[1] - xi[0]) ** 2;
            const dtheta_dxi = (theta.slice(1) - 2 * theta.slice(0, -1) + theta.slice(0, -2)) / (xi[1] - xi[0]) ** 2;
            const exp_term = Math.exp(gamma * (1 - 1 / theta));
            const dc_dt = dc_dxi.slice(1) - phi ** 2 * c.slice(1, -1) * exp_term;
            const dtheta_dt = dtheta_dxi.slice(1) + beta * phi ** 2 * c.slice(1, -1) * exp_term;
            return [
                [0, ...dc_dt, 0],
                [0, ...dtheta_dt, 0]
            ];
