    <script id="MathJax-script" async src="https://cdn.jsdelivr.net/npm/mathjax@3/es5/tex-mml-chtml.js"></script>
</head>
<body>
    <div id="main-container">
        <h1>Richards Growth Curve</h1>
        <p>This demo visualizes the Richards growth curve and its 4/5 law approximation based on adjustable parameters.</p>
        <div id="control-panel">
            <div class="slider-wrapper">
                <label>maximum biomass \(b_{max}\)</label>
                <input type="range" id="slider-bmax" min="1000" max="2500" step="1" value="1566">
                <span>+</span>
                <span id="value-bmax">1566</span>
            </div>
            <div class="slider-wrapper">
                <label>initial biomass \(b_0\)</label>
                <input type="range" id="slider-b0" min="1" max="100" step="0.1" value="30">
                <span>+</span>
                <span id="value-b0">30</span>
            </div>
            <div class="slider-wrapper">
                <label>intrinsic growth rate \(r\)</label>
                <input type="range" id="slider-r" min="0.01" max="0.2" step="0.001" value="0.113">
                <span>+</span>
                <span id="value-r">0.113</span>
            </div>
            <div class="slider-wrapper">
                <label>shape exponent \(\beta\)</label>
                <input type="range" id="slider-beta" min="0.1" max="1.5" step="0.001" value="0.477">
                <span>+</span>
                <span id="value-beta">0.477</span>
            </div>
        </div>
        <div id="visualization-area">
            <div class="flex-container">
                <div id="plot-container"></div>
                <div id="table-container">
                    <table>
                        <thead>
                            <tr>
                                <th>Age (years)</th>
                                <th>Volume (m³ ha⁻¹)</th>
                            </tr>
                        </thead>
                        <tbody>
                            <tr><td>30</td><td>289</td></tr>
                            <tr><td>35</td><td>445</td></tr>
                            <tr><td>40</td><td>565</td></tr>
                            <tr><td>45</td><td>704</td></tr>
                            <tr><td>50</td><td>833</td></tr>
                            <tr><td>55</td><td>924</td></tr>
                        </tbody>
                    </table>
                </div>
            </div>
            <div id="results-display">
                <p>rejuvenation bias u = <span id="result-u">0.0000</span></p>
                <p>site productivity p = <span id="result-p">0.0000</span></p>
            </div>
        </div>
    </div>

    <script>
        const ages = [30, 35, 40, 45, 50, 55];
        const volumes = [289, 445, 565, 704, 833, 924];
        const tValues = Array.from({ length: 66 }, (_, i) => 15 + i);

        function richardsCurve(t, bMax, b0, r, beta) {
            const M = ((bMax / b0) ** beta) - 1;
            return bMax / (1 + M * Math.exp(-r * t)) ** (1 / beta);
        }

        function calculate4_5LawParameters(bMax, b0, r, beta) {
            const M = ((bMax / b0) ** beta) - 1;
            const ti = Math.log(M * beta) / r;
            const Vi = bMax * (((beta + 1) / (2 * beta)) ** (1 / beta));
            const ViPrime = (r / beta) * Vi * (1 - (Vi / bMax) ** beta);
            const u = ti - (4 / 5) * Vi / ViPrime;
            const p = Vi / (ti - u) ** (4 / 5);
            return { u, p, ti, Vi, ViPrime };
        }

        function update() {
            const bMax = parseFloat(document.getElementById('slider-bmax').value);
            const b0 = parseFloat(document.getElementById('slider-b0').value);
            const r = parseFloat(document.getElementById('slider-r').value);
            const beta = parseFloat(document.getElementById('slider-beta').value);

            document.getElementById('value-bmax').textContent = bMax.toFixed(0);
            document.getElementById('value-b0').textContent = b0.toFixed(1);
            document.getElementById('value-r').textContent = r.toFixed(3);
            document.getElementById('value-beta').textContent = beta.toFixed(3);

            const richardsValues = tValues.map(t => richardsCurve(t, bMax, b0, r, beta));

            const { u, p } = calculate4_5LawParameters(bMax, b0, r, beta);
            document.getElementById('result-u').textContent = u.toFixed(4);
            document.getElementById('result-p').textContent = p.toFixed(4);

            const fourFiveLawValues = tValues.map(t => {
                return t > u ? p * (t - u) ** (4 / 5) : null;
            });

            const traceDataPoints = {
                x: ages,
                y: volumes,
                mode: 'markers',
                marker: { color: 'blue' },
                name: 'Data Points'
            };

            const traceRichardsCurve = {
                x: tValues,
                y: richardsValues,
                mode: 'lines',
                line: { color: 'orange' },
                name: 'Richards Curve'
            };

            const traceFourFiveLawCurve = {
                x: tValues,
                y: fourFiveLawValues,
                mode: 'lines',
                line: { color: 'blue' },
                name: '4/5 Law Curve'
            };

            const layout = {
                title: 'forest growth curve',
                xaxis: {
                    title: 'stand age (years)',
                    range: [15, 80],
                    gridcolor: 'lightgrey',
                    zeroline: false,
                    ticklen: 4,
                    mirror: true
                },
                yaxis: {
                    title: 'wood volume, m³ ha⁻¹<br>(Richards curve vs 4/5 law)',
                    range: [0, 1600],
                    gridcolor: 'lightgrey',
                    zeroline: false,
                    ticklen: 4,
                    mirror: true
                },
                plot_bgcolor: '#FFFFF0',
                showlegend: false
            };

            Plotly.newPlot('plot-container', [traceDataPoints, traceRichardsCurve, traceFourFiveLawCurve], layout);
        }

        document.addEventListener('DOMContentLoaded', () => {
            document.querySelectorAll('#control-panel input[type="range"]').forEach(slider => {
                slider.addEventListener('input', update);
            });
            update();
        });
    </script>
</body>
