        const canvas = document.getElementById('main-canvas');
        const scene = new THREE.Scene();
        scene.background = new THREE.Color(0xffffff);

        const camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
        camera.position.z = 200;

        const renderer = new THREE.WebGLRenderer({ canvas });
        renderer.setSize(window.innerWidth, window.innerHeight);

        const ambientLight = new THREE.AmbientLight(0x404040);
        scene.add(ambientLight);

        const directionalLight = new THREE.DirectionalLight(0xffffff, 0.5);
        directionalLight.position.set(1, 1, 1).normalize();
        scene.add(directionalLight);

        const group = new THREE.Group();
        scene.add(group);

        function createPolygonGeometry(sides) {
            const shape = new THREE.Shape();
            const angleStep = (Math.PI * 2) / sides;
            const radius = 10;
            for (let i = 0; i < sides; i++) {
                const angle = angleStep * i;
                const x = radius * Math.cos(angle);
                const y = radius * Math.sin(angle);
                if (i === 0) {
                    shape.moveTo(x, y);
                } else {
                    shape.lineTo(x, y);
                }
            }
            return new THREE.ShapeGeometry(shape);
        }

        function redraw() {
            const numSides = parseInt(document.querySelector('input[name="base-polygon"]:checked').value);
            const numUnits = parseInt(document.getElementById('slider-units').value);
            const angularAdvance = parseFloat(document.getElementById('slider-angle').value);
            const makeConical = parseFloat(document.getElementById('slider-conical').value);
            const rotateX = parseFloat(document.getElementById('slider-rotate-x').value);
            const rotateZ = parseFloat(document.getElementById('slider-rotate-z').value);

            while (group.children.length > 0) {
                group.remove(group.children[0]);
            }

            const geometry = createPolygonGeometry(numSides);
            const angularAdvanceRad = angularAdvance * (Math.PI / 180);

            for (let i = 0; i < numUnits; i++) {
                const angle = i * angularAdvanceRad;
                const radius = 4 * Math.sqrt(i);
                const scale = 0.5 * Math.log(i + 2);
                const x = radius * Math.cos(angle);
                const y = radius * Math.sin(angle);
                const z = makeConical * radius;

                const material = new THREE.MeshStandardMaterial({ flatShading: true });
                material.color.setHSL((angle / (2 * Math.PI)) % 1.0, 0.8, 0.6);

                const mesh = new THREE.Mesh(geometry, material);
                mesh.position.set(x, y, z);
                mesh.scale.set(scale, scale, scale);
                mesh.rotation.z = angle;
                group.add(mesh);
            }

            group.rotation.x = rotateX * (Math.PI / 180);
            group.rotation.z = rotateZ * (Math.PI / 180);

            renderer.render(scene, camera);
        }

        document.querySelectorAll('input[name="base-polygon"]').forEach(radio => {
            radio.addEventListener('change', redraw);
        });

        document.querySelectorAll('input[type="range"]').forEach(slider => {
            slider.addEventListener('input', redraw);
        });

        document.querySelectorAll('button').forEach(button => {
            button.addEventListener('click', () => {
                const sliderId = button.id.replace('-plus', '');
                const slider = document.getElementById(sliderId);
                slider.value = parseFloat(slider.value) + parseFloat(slider.step);
                redraw();
            });
        });

        window.addEventListener('resize', () => {
            camera.aspect = window.innerWidth / window.innerHeight;
            camera.updateProjectionMatrix();
            renderer.setSize(window.innerWidth, window.innerHeight);
            redraw();
        });

        redraw();
    </script>
</body>
</html>
