    <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r150/three.min.js"></script>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r150/examples/js/controls/OrbitControls.js"></script>
    <script>
        const scene = new THREE.Scene();
        scene.background = new THREE.Color(0xf0f0f0);

        const camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 10);
        camera.position.z = 2;

        const renderer = new THREE.WebGLRenderer({ antialias: true });
        renderer.setSize(window.innerWidth, window.innerHeight);
        document.getElementById('canvas-container').appendChild(renderer.domElement);

        const orbitControls = new THREE.OrbitControls(camera, renderer.domElement);
        orbitControls.enableDamping = true;
        orbitControls.dampingFactor = 0.25;
        orbitControls.enableZoom = true;

        const light = new THREE.AmbientLight(0x404040); // soft white light
        scene.add(light);

        const directionalLight = new THREE.DirectionalLight(0xffffff, 0.5);
        directionalLight.position.set(1, 1, 1).normalize();
        scene.add(directionalLight);

        const gridHelper = new THREE.GridHelper(1, 10, 0x888888, 0x444444);
        scene.add(gridHelper);

        const axesHelper = new THREE.AxesHelper(1);
        scene.add(axesHelper);

        const unitCellGeometry = new THREE.EdgesGeometry(new THREE.BoxGeometry(1, 1, 1));
        const unitCellMaterial = new THREE.LineBasicMaterial({ color: 0x000000 });
        const unitCell = new THREE.LineSegments(unitCellGeometry, unitCellMaterial);
        scene.add(unitCell);

        const atomGroup = new THREE.Group();
        scene.add(atomGroup);

        const planeGroup = new THREE.Group();
        scene.add(planeGroup);

        function gcd(a, b) {
            while (b !== 0) {
                [a, b] = [b, a % b];
            }
            return a;
        }

        function gcd3(h, k, l) {
            return gcd(gcd(h, k), l);
        }

        function createSphere(position) {
            const sphereGeometry = new THREE.SphereGeometry(0.05, 32, 32);
            const sphereMaterial = new THREE.MeshPhongMaterial({
                color: 0x008080,
                specular: 0x111111,
                shininess: 100
            });
            const sphere = new THREE.Mesh(sphereGeometry, sphereMaterial);
            sphere.position.set(...position);
            return sphere;
        }

        function updateScene() {
            atomGroup.clear();
            planeGroup.clear();

            const latticeType = document.querySelector('#lattice-controls button.active').id.slice(4).toUpperCase();
            const h = parseInt(document.getElementById('input-h').value);
            const k = parseInt(document.getElementById('input-k').value);
            const l = parseInt(document.getElementById('input-l').value);
            const opacity = parseFloat(document.getElementById('slider-opacity').value);

            let positions = [];
            switch (latticeType) {
                case 'SC':
                    positions = [
                        [-0.5, -0.5, -0.5], [0.5, -0.5, -0.5], [-0.5, 0.5, -0.5], [0.5, 0.5, -0.5],
                        [-0.5, -0.5, 0.5], [0.5, -0.5, 0.5], [-0.5, 0.5, 0.5], [0.5, 0.5, 0.5]
                    ];
                    break;
                case 'BCC':
                    positions = [
                        [-0.5, -0.5, -0.5], [0.5, -0.5, -0.5], [-0.5, 0.5, -0.5], [0.5, 0.5, -0.5],
                        [-0.5, -0.5, 0.5], [0.5, -0.5, 0.5], [-0.5, 0.5, 0.5], [0.5, 0.5, 0.5],
                        [0, 0, 0]
                    ];
                    break;
                case 'FCC':
                    positions = [
                        [-0.5, -0.5, -0.5], [0.5, -0.5, -0.5], [-0.5, 0.5, -0.5], [0.5, 0.5, -0.5],
                        [-0.5, -0.5, 0.5], [0.5, -0.5, 0.5], [-0.5, 0.5, 0.5], [0.5, 0.5, 0.5],
                        [0.5, 0, 0], [-0.5, 0, 0], [0, 0.5, 0], [0, -0.5, 0], [0, 0, 0.5], [0, 0, -0.5]
                    ];
                    break;
            }

            positions.forEach(pos => {
                const sphere = createSphere(pos);
                atomGroup.add(sphere);
            });

            if (h === 0 && k === 0 && l === 0) return;

            const g = gcd3(h, k, l);
            const hp = h / g;
            const kp = k / g;
            const lp = l / g;

            const vertices = [
                [-0.5, -0.5, -0.5], [-0.5, -0.5, 0.5], [-0.5, 0.5, -0.5], [-0.5, 0.5, 0.5],
                [0.5, -0.5, -0.5], [0.5, -0.5, 0.5], [0.5, 0.5, -0.5], [0.5, 0.5, 0.5]
            ];

            const minN = Math.floor(vertices.reduce((min, v) => Math.min(min, hp * v[0] + kp * v[1] + lp * v[2]), Infinity));
            const maxN = Math.ceil(vertices.reduce((max, v) => Math.max(max, hp * v[0] + kp * v[1] + lp * v[2]), -Infinity));

            for (let n = minN; n <= maxN; n++) {
                const intersections = [];
                const edges = [
                    [[-0.5, -0.5, -0.5], [-0.5, -0.5, 0.5]],
                    [[-0.5, -0.5, -0.5], [-0.5, 0.5, -0.5]],
                    [[-0.5, -0.5, -0.5], [0.5, -0.5, -0.5]],
                    [[-0.5, 0.5, -0.5], [-0.5, 0.5, 0.5]],
                    [[-0.5, 0.5, -0.5], [0.5, 0.5, -0.5]],
                    [[-0.5, -0.5, 0.5], [-0.5, 0.5, 0.5]],
                    [[-0.5, -0.5, 0.5], [0.5, -0.5, 0.5]],
                    [[0.5, -0.5, -0.5], [0.5, -0.5, 0.5]],
                    [[0.5, -0.5, -0.5], [0.5, 0.5, -0.5]],
                    [[0.5, 0.5, -0.5], [0.5, 0.5, 0.5]],
                    [[-0.5, 0.5, 0.5], [0.5, 0.5, 0.5]],
                    [[-0.5, -0.5, 0.5], [0.5, -0.5, 0.5]]
                ];

                edges.forEach(edge => {
                    const [p1, p2] = edge;
                    const t = (n - (hp * p1[0] + kp * p1[1] + lp * p1[2])) / (hp * (p2[0] - p1[0]) + kp * (p2[1] - p1[1]) + lp * (p2[2] - p1[2]));
                    if (t >= 0 && t <= 1) {
                        const x = p1[0] + t * (p2[0] - p1[0]);
                        const y = p1[1] + t * (p2[1] - p1[1]);
                        const z = p1[2] + t * (p2[2] - p1[2]);
                        intersections.push([x, y, z]);
                    }
                });

                if (intersections.length >= 3) {
                    const centroid = intersections.reduce((sum, p) => [sum[0] + p[0], sum[1] + p[1], sum[2] + p[2]], [0, 0, 0]).map(c => c / intersections.length);
                    intersections.sort((a, b) => {
                        const angleA = Math.atan2(a[1] - centroid[1], a[0] - centroid[0]);
                        const angleB = Math.atan2(b[1] - centroid[1], b[0] - centroid[0]);
                        return angleA - angleB;
                    });

                    const shape = new THREE.Shape();
                    shape.moveTo(intersections[0][0], intersections[0][1]);
                    for (let i = 1; i < intersections.length; i++) {
                        shape.lineTo(intersections[i][0], intersections[i][1]);
                    }
                    shape.closePath();

                    const geometry = new THREE.ShapeGeometry(shape);
                    const material = new THREE.MeshBasicMaterial({
                        color: 0x0000ff,
                        transparent: true,
                        opacity: opacity,
                        side: THREE.DoubleSide
                    });
                    const plane = new THREE.Mesh(geometry, material);
                    plane.position.setZ(intersections[0][2]);
                    plane.rotation.x = Math.PI / 2;
                    planeGroup.add(plane);
                }
            }
        }

        document.querySelectorAll('#lattice-controls button').forEach(btn => {
            btn.addEventListener('click', () => {
                document.querySelectorAll('#lattice-controls button').forEach(b => b.classList.remove('active'));
                btn.classList.add('active');
                updateScene();
            });
        });

        document.getElementById('input-h').addEventListener('input', updateScene);
        document.getElementById('input-k').addEventListener('input', updateScene);
        document.getElementById('input-l').addEventListener('input', updateScene);

        document.getElementById('slider-opacity').addEventListener('input', () => {
            const opacity = parseFloat(document.getElementById('slider-opacity').value);
            planeGroup.children.forEach(plane => {
                plane.material.opacity = opacity;
                plane.material.needsUpdate = true;
            });
        });

        function animate() {
            requestAnimationFrame(animate);
            orbitControls.update();
            renderer.render(scene, camera);
        }

        updateScene();
        animate();

        window.addEventListener('resize', () => {
            camera.aspect = window.innerWidth / window.innerHeight;
            camera.updateProjectionMatrix();
            renderer.setSize(window.innerWidth, window.innerHeight);
        });
    </script>
</body>
