            points.forEach(([x, y, z]) => {
                const theta = Math.atan2(y, x);
                const phi = Math.acos(z);
                const u = (theta + Math.PI) / (Math.PI * 2);
                const v = phi / Math.PI;
                const px = u * canvas.width;
                const py = v * canvas.height;
                ctx.beginPath();
                ctx.arc(px, py, 3, 0, 2 * Math.PI);
                ctx.fillStyle = '#0000ff';
                ctx.fill();
            });
        }

        function calculateMinDistance(points) {
            let minDistance = Infinity;
            for (let i = 0; i < points.length; i++) {
                for (let j = i + 1; j < points.length; j++) {
                    const dx = points[i][0] - points[j][0];
                    const dy = points[i][1] - points[j][1];
                    const dz = points[i][2] - points[j][2];
                    const distance = Math.sqrt(dx * dx + dy * dy + dz * dz);
                    if (distance < minDistance) {
                        minDistance = distance;
                    }
                }
            }
            return minDistance;
        }

        function updatePoints(numPoints) {
            const points = THOMSON_POINTS[numPoints] || generateRandomPointsOnSphere(numPoints);
            createPointMeshes(points);
            if (document.getElementById('btn-3d').classList.contains('active')) {
                render3D();
            } else {
                render2D(points);
            }
        }

        document.getElementById('slider-points').addEventListener('input', (event) => {
            const numPoints = parseInt(event.target.value, 10);
            document.getElementById('label-points').innerText = `+ ${numPoints}`;
            updatePoints(numPoints);
        });

        document.getElementById('btn-3d').addEventListener('click', () => {
            document.getElementById('btn-3d').classList.add('active');
            document.getElementById('btn-2d').classList.remove('active');
            document.getElementById('div-2d-plot').style.display = 'none';
            document.getElementById('canvas-3d').style.display = 'block';
            render3D();
        });

        document.getElementById('btn-2d').addEventListener('click', () => {
            document.getElementById('btn-2d').classList.add('active');
            document.getElementById('btn-3d').classList.remove('active');
            document.getElementById('div-2d-plot').style.display = 'block';
            document.getElementById('canvas-3d').style.display = 'none';
            const numPoints = parseInt(document.getElementById('slider-points').value, 10);
            const points = THOMSON_POINTS[numPoints] || generateRandomPointsOnSphere(numPoints);
            render2D(points);
        });

        updatePoints(20);

        function animate() {
            requestAnimationFrame(animate);
            if (document.getElementById('btn-3d').classList.contains('active')) {
                render3D();
            }
            controls.update();
        }

        animate();
    </script>
</body>
</html>
