<body>
    <div class="main-container">
        <div class="control-panel">
            <div class="control-group">
                <label for="slider-angle">angle relative to ecliptic</label>
                <input type="range" id="slider-angle" min="0" max="60" step="0.01" value="23.45">
                <span id="value-angle">23.45</span>
            </div>
            <div class="control-group">
                <label for="slider-longitude">longitude of periapsis</label>
                <input type="range" id="slider-longitude" min="0" max="360" step="0.01" value="102.95">
                <span id="value-longitude">102.95</span>
            </div>
            <div class="control-group">
                <label for="slider-eccentricity">orbit eccentricity</label>
                <input type="range" id="slider-eccentricity" min="0" max="0.99" step="0.000001" value="0.016722">
                <span id="value-eccentricity">0.016722</span>
            </div>
        </div>
        <div class="visualization-area">
            <canvas id="orbit-canvas"></canvas>
            <canvas id="plot-canvas"></canvas>
        </div>
    </div>

    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.min.js"></script>
    <script>
        let angleSlider, longitudeSlider, eccentricitySlider;
        let angleValue, longitudeValue, eccentricityValue;

        function setup() {
            createCanvas(600, 300);
            angleSlider = select('#slider-angle');
            longitudeSlider = select('#slider-longitude');
            eccentricitySlider = select('#slider-eccentricity');

            angleValue = float(angleSlider.value());
            longitudeValue = float(longitudeSlider.value());
            eccentricityValue = float(eccentricitySlider.value());

            angleSlider.input(updateValues);
            longitudeSlider.input(updateValues);
            eccentricitySlider.input(updateValues);

            updateValues();
        }

        function updateValues() {
            angleValue = float(angleSlider.value());
            longitudeValue = float(longitudeSlider.value());
            eccentricityValue = float(eccentricitySlider.value());

            select('#value-angle').html(angleValue.toFixed(2));
            select('#value-longitude').html(longitudeValue.toFixed(2));
            select('#value-eccentricity').html(eccentricityValue.toFixed(6));

            redraw();
        }

        function draw() {
            background(240);
            translate(width / 2, height / 2);
            drawOrbit();
            drawEquationOfTimePlot();
        }

        function drawOrbit() {
            let a = 0.4 * width;
            let b = a * sqrt(1 - pow(eccentricityValue, 2));
            let angleRad = radians(longitudeValue);

            push();
            rotate(angleRad);
            noFill();
            stroke(160, 82, 45);
            ellipse(0, 0, 2 * a, 2 * b);
            pop();
        }

        function drawEquationOfTimePlot() {
            let plotWidth = width;
            let plotHeight = height;
            let margin = 40;

            let xMin = 0;
            let xMax = TWO_PI;
            let yMin = -20;
            let yMax = 20;

            let points = [];
            let previousEOT = 0;

            for (let M = xMin; M <= xMax; M += 0.1) {
                let E = solveKeplersEquation(M, eccentricityValue);
                let v = 2 * atan2(sqrt(1 + eccentricityValue) * sin(E / 2), sqrt(1 - eccentricityValue) * cos(E / 2));
                let lambda = v + radians(longitudeValue);
                let alpha = atan2(cos(radians(angleValue)) * sin(lambda), cos(lambda));
                let EOT_rad = M - alpha;

                // Phase unwrapping
                if (abs(EOT_rad - previousEOT) > PI) {
                    EOT_rad += (EOT_rad > previousEOT) ? -TWO_PI : TWO_PI;
                }
                previousEOT = EOT_rad;

                let EOT_minutes = EOT_rad * (720 / PI);
                points.push([M, EOT_minutes]);
            }

            yMin = min(points.map(p => p[1])) - 1;
            yMax = max(points.map(p => p[1])) + 1;

            background(255);
            stroke(0);
            line(margin, plotHeight - margin, plotWidth - margin, plotHeight - margin); // X-axis
            line(margin, margin, margin, plotHeight - margin); // Y-axis

            textSize(12);
            textAlign(CENTER, CENTER);
            for (let i = 1; i <= 6; i++) {
                let x = map(i * PI / 3, xMin, xMax, margin, plotWidth - margin);
                line(x, plotHeight - margin, x, plotHeight - margin + 5);
                text(i, x, plotHeight - margin + 15);
            }

            textAlign(RIGHT, CENTER);
            for (let i = -20; i <= 20; i += 5) {
                let y = map(i, yMin, yMax, plotHeight - margin, margin);
                line(margin, y, margin - 5, y);
                text(i, margin - 10, y);
            }

            stroke(255, 140, 0);
            beginShape();
            for (let [M, EOT] of points) {
                let x = map(M, xMin, xMax, margin, plotWidth - margin);
                let y = map(EOT, yMin, yMax, plotHeight - margin, margin);
                vertex(x, y);
            }
            endShape();
        }

        function solveKeplersEquation(M, e) {
            let E = M;
            for (let i = 0; i < 10; i++) {
                E = E - (E - e * sin(E) - M) / (1 - e * cos(E));
            }
            return E;
        }
    </script>
</body>
</html>
