    <script>
        let c = 4;
        let h_C = 1.9;
        let delta = 0.5;
        let plotRange = 1;
        let step = 5;
        let verification = true;

        function setup() {
            const canvas = createCanvas(windowWidth * 0.7, windowHeight);
            canvas.parent('visualization-area');
            noLoop();
            redrawScene();
        }

        function draw() {}

        function redrawScene() {
            clear();
            background(224);

            // Coordinate system setup
            translate(width / 2, height * 0.6);
            scale(1, -1);
            scale(50 * plotRange);

            // Initial points
            const F = { x: 0, y: 0 };
            const C = { x: 0, y: h_C };
            const D = { x: c / 2, y: 0 };
            const K = { x: c, y: 0 };

            // Solve for M
            const a = 1 + tan(delta) * tan(delta);
            const b = -2 * h_C * tan(delta) - c;
            const c = h_C * h_C - c * c / 4;

            const discriminant = b * b - 4 * a * c;
            const y_M = (-b - sqrt(discriminant)) / (2 * a);
            const x_M = (c / 2 - h_C * y_M) / (h_C - y_M);

            const M = { x: x_M, y: y_M };
            const R = dist(M.x, M.y, C.x, C.y);

            // Solve for B
            const x_B = 2 * M.x - c / 2;
            const B = { x: x_B, y: 0 };

            // Solve for A
            const A = { x: x_B - c, y: 0 };

            // Verification point E
            const E = { x: c - x_B, y: 0 };

            // Drawing logic
            if (step >= 1) {
                // Line λ
                strokeWeight(1);
                stroke(0);
                line(-width, 0, width, 0);

                // Points F, C, D and line FC
                drawPoint(F, 'F');
                drawPoint(C, 'C');
                drawPoint(D, 'D');
                line(F.x, F.y, C.x, C.y);
            }

            if (step >= 2) {
                // Point K and dashed lines CD, DK
                drawPoint(K, 'K');
                strokeWeight(1);
                stroke(0);
                drawingContext.setLineDash([5, 5]);
                line(C.x, C.y, D.x, D.y);
                line(D.x, D.y, K.x, K.y);
                drawingContext.setLineDash([]);

                // Point M and dashed lines MC, MD, MK, arc κ
                drawPoint(M, 'M');
                drawingContext.setLineDash([5, 5]);
                line(M.x, M.y, C.x, C.y);
                line(M.x, M.y, D.x, D.y);
                line(M.x, M.y, K.x, K.y);
                drawingContext.setLineDash([]);
                arc(M.x, M.y, 2 * R, 0, TWO_PI);
            }

            if (step >= 3) {
                // Point B
                drawPoint(B, 'B');
            }

            if (step >= 4) {
                // Point A
                drawPoint(A, 'A');
            }

            if (step >= 5) {
                // Triangle ABC
                strokeWeight(3);
                line(A.x, A.y, B.x, B.y);
                line(B.x, B.y, C.x, C.y);
                line(C.x, C.y, A.x, A.y);
                strokeWeight(1);
            }

            if (verification) {
                // Point E and dashed lines CE, KB
                drawPoint(E, 'E');
                strokeWeight(1);
                stroke(0);
                drawingContext.setLineDash([5, 5]);
                line(C.x, C.y, E.x, E.y);
                line(K.x, K.y, B.x, B.y);
                drawingContext.setLineDash([]);
            }
        }

        function drawPoint(point, label) {
            fill(0);
            ellipse(point.x, point.y, 0.1, 0.1);
            noStroke();
            fill(0);
            push();
            resetMatrix();
            textAlign(CENTER, CENTER);
            text(label, (point.x * 50 * plotRange) + width / 2, (height * 0.6) - (point.y * 50 * plotRange));
            pop();
        }

        document.getElementById('slider-c').addEventListener('input', function() {
            c = parseFloat(this.value);
            document.getElementById('c-value').textContent = c.toFixed(1);
            redrawScene();
        });

        document.getElementById('slider-h_C').addEventListener('input', function() {
            h_C = parseFloat(this.value);
            document.getElementById('h_C-value').textContent = h_C.toFixed(1);
            redrawScene();
        });

        document.getElementById('slider-delta').addEventListener('input', function() {
            delta = parseFloat(this.value);
            document.getElementById('delta-value').textContent = delta.toFixed(2);
            redrawScene();
        });

        document.querySelectorAll('input[name="steps"]').forEach(radio => {
            radio.addEventListener('change', function() {
                step = parseInt(this.value);
                redrawScene();
            });
        });

        document.getElementById('check-verification').addEventListener('change', function() {
            verification = this.checked;
            redrawScene();
        });

        document.getElementById('slider-plot-range').addEventListener('input', function() {
            plotRange = parseFloat(this.value);
            document.getElementById('plot-range-value').textContent = plotRange.toFixed(2);
            redrawScene();
        });
