</head>
<body>
    <div id="main-container">
        <div id="control-panel">
            <label for="slider-z">z (m)</label>
            <input type="range" id="slider-z" min="0" max="32" step="0.01" value="0">
            <span id="label-z">0.</span>
        </div>
        <div id="plots-container">
            <div id="plot-intensity"></div>
            <div id="plot-population"></div>
            <div id="plot-coherence"></div>
        </div>
    </div>

    <script>
        const t_values = Array.from({ length: 301 }, (_, i) => i - 50);

        // Red curves data (static)
        const I_red = t_values.map(t => (t < -15 || t > 170) ? 0 : (t > 15 ? 1 : 0.5 * (1 + Math.tanh(t / 4))));
        const P_red = t_values.map(t => (t < -15) ? 0 : 4.5 * (1 - Math.exp(-(t + 15) / 50)));
        const C_red = t_values.map(t => (t < -15) ? 0 : 2.2 * (1 - Math.exp(-(t + 15) / 50)));

        // Initial plot setup
        function createPlot(divId, y_red, y_label, y_range) {
            const data = [
                {
                    x: t_values,
                    y: y_red,
                    name: "z = 0 meters",
                    mode: 'lines',
                    line: { color: 'red', width: 2 }
                },
                {
                    x: t_values,
                    y: y_red, // Initially same as red
                    name: "z = 0. meters",
                    mode: 'lines',
                    line: { color: 'blue', width: 2, dash: 'dash' }
                }
            ];

            const layout = {
                xaxis: { title: "time (ns)", range: [-50, 250], zeroline: true },
                yaxis: { title: y_label, range: y_range, zeroline: true },
                margin: { l: 80, r: 20, t: 40, b: 40 },
                showlegend: true,
                legend: { x: 0.7, y: 0.9 },
                shapes: [
                    { type: 'line', x0: 0, y0: 0, x1: 0, y1: 1, yref: 'paper', line: { color: 'black', width: 2, dash: 'dash' } },
                    { type: 'line', x0: 0, y0: 0, x1: 0, y1: 1, yref: 'paper', line: { color: 'black', width: 2, dash: 'dash' } }
                ],
                annotations: [
                    { x: -45, y: 0.9, xref: 'x', yref: 'y', text: divId === 'plot-intensity' ? '(a)' : (divId === 'plot-population' ? '(b)' : '(c)'), showarrow: false },
                    { x: 0, y: 0.7, xref: 'x', yref: 'y', text: 'pulse<br>midpoint', showarrow: false, xanchor: 'left', yanchor: 'bottom' }
                ]
            };

            Plotly.newPlot(divId, data, layout);
        }

        createPlot('plot-intensity', I_red, 'intensity (arb)', [-0.1, 1.1]);
        createPlot('plot-population', P_red, 'excited-state population × 10<sup>4</sup>', [-0.5, 5.5]);
        createPlot('plot-coherence', C_red, 'coherence × 10<sup>2</sup>', [-0.2, 2.4]);

        // Update plots based on z value
        function updatePlots(z) {
            const t_delay = z / 0.3;

            // Blue curves data (dynamic)
            const I_blue = t_values.map((t, i) => {
                const t_prime = t - t_delay;
                const I_base = (t_prime < -15) ? 0 : (t_prime > 15 ? 1 : 0.5 * (1 + Math.tanh(t_prime / 4)));
                const envelope = (t_prime > 0) ? Math.exp(-0.035 * z * t_prime / 32) : 1;
                return (t > 170 + t_delay) ? 0 : I_base * envelope;
            });

            const scaling_factor_p = 1 - 0.45 * (z / 32);
            const P_blue = t_values.map((t, i) => {
                const t_prime = t - t_delay;
                return (t > 170 + t_delay) ? 0 : scaling_factor_p * ((t_prime < -15) ? 0 : 4.5 * (1 - Math.exp(-(t_prime + 15) / 50)));
            });

            const C_blue = t_values.map((t, i) => {
                const t_prime = t - t_delay;
                return (t > 170 + t_delay) ? 0 : scaling_factor_c * ((t_prime < -15) ? 0 : 2.2 * (1 - Math.exp(-(t_prime + 15) / 50)));
            });

            // Update traces
            Plotly.restyle('plot-intensity', 'y', [I_red, I_blue]);
            Plotly.restyle('plot-population', 'y', [P_red, P_blue]);
            Plotly.restyle('plot-coherence', 'y', [C_red, C_blue]);

            // Update legend
            Plotly.relayout('plot-intensity', 'data[1].name', `z = ${z.toFixed(2)} meters`);
            Plotly.relayout('plot-population', 'data[1].name', `z = ${z.toFixed(2)} meters`);
            Plotly.relayout('plot-coherence', 'data[1].name', `z = ${z.toFixed(2)} meters`);

            // Update moving vertical line
            Plotly.relayout('plot-intensity', 'shapes[1].x0', t_delay);
            Plotly.relayout('plot-intensity', 'shapes[1].x1', t_delay);
            Plotly.relayout('plot-population', 'shapes[1].x0', t_delay);
            Plotly.relayout('plot-population', 'shapes[1].x1', t_delay);
            Plotly.relayout('plot-coherence', 'shapes[1].x0', t_delay);
            Plotly.relayout('plot-coherence', 'shapes[1].x1', t_delay);
        }

        // Initial state
        updatePlots(0);

        // Slider interaction
        document.getElementById('slider-z').addEventListener('input', function() {
            const z_current = parseFloat(this.value);
            document.getElementById('label-z').textContent = z_current.toFixed(2) + (z_current % 1 === 0 ? '.' : '');
            updatePlots(z_current);
        });
    </script>
</body>
</html>
