    </style>
</head>
<body>
    <div id="demo-container">
        <!-- Control Panel Section -->
        <div id="control-panel">
            <!-- Heat Transfer Coefficient Slider -->
            <div>
                <label for="slider-ua">heat transfer coefficient (cal/(dm² K s))</label>
                <input type="range" id="slider-ua" min="0" max="20" step="0.1" value="0">
                <span id="ua-value">0</span>
            </div>

            <!-- Reverse Reaction Pre-exponential Factor Slider -->
            <div>
                <label for="slider-kr0">reverse reaction pre-exponential factor (1/s)</label>
                <input type="range" id="slider-kr0" min="0" max="10" step="0.1" value="0">
                <span id="kr0-value">0</span>
            </div>

            <!-- Feed Temperature Slider -->
            <div>
                <label for="slider-tf">feed temperature (K)</label>
                <input type="range" id="slider-tf" min="250" max="350" step="1" value="265">
                <span id="tf-value">265</span>
            </div>

            <!-- Residence Time Slider -->
            <div>
                <label for="slider-tau">residence time (s)</label>
                <input type="range" id="slider-tau" min="100" max="1000" step="10" value="400">
                <span id="tau-value">400</span>
            </div>
        </div>

        <!-- Visualization Section -->
        <div id="plot-container"></div>
    </div>

    <!-- CDN for Plotly.js -->
    <script src="https://cdn.plot.ly/plotly-latest.min.js"></script>
    <script>
        const C_A0 = 10; // mmol/dm³, initial concentration of A
        const R = 1.987; // cal/mol K, ideal gas constant
        const E_f = 25000; // cal/mol, forward activation energy
        const ΔH_rxn = -200000; // cal/mol, enthalpy of reaction
        const E_r = E_f - ΔH_rxn; // cal/mol, reverse activation energy
        const k_f0 = 1e15; // 1/s, forward pre-exponential factor
        const rhoCp = 20000; // cal/(dm³ K), product of density and heat capacity

        const tempPoints = Array.from({ length: 151 }, (_, i) => 250 + i * 1);

        function calculateConcentrations(UA, kr0, Tf, tau) {
            return tempPoints.map(T => {
                const kf = k_f0 * Math.exp(-E_f / (R * T));
                const kr = kr0 * 1e12 * Math.exp(-E_r / (R * T));
                const massBalanceCB = (tau * kf * C_A0) / (1 + tau * (kf + kr));
                const energyBalanceCB = (rhoCp * (T - Tf) + UA * tau * (T - Tf)) / (-ΔH_rxn);
                return { T, massBalanceCB, energyBalanceCB };
            });
        }

        function updatePlot() {
            const UA = parseFloat(document.getElementById('slider-ua').value);
            const kr0 = parseFloat(document.getElementById('slider-kr0').value);
            const Tf = parseFloat(document.getElementById('slider-tf').value);
            const tau = parseFloat(document.getElementById('slider-tau').value);

            document.getElementById('ua-value').textContent = UA.toFixed(1);
            document.getElementById('kr0-value').textContent = `${(kr0 * 1e12).toExponential(1)} x 10¹²`;
            document.getElementById('tf-value').textContent = Tf;
            document.getElementById('tau-value').textContent = tau;

            const concentrations = calculateConcentrations(UA, kr0, Tf, tau);

            const massBalanceTrace = {
                x: concentrations.map(c => c.T),
                y: concentrations.map(c => c.massBalanceCB),
                mode: 'lines',
                name: 'mass balance',
                line: { color: '#008000' }
            };

            const energyBalanceTrace = {
                x: concentrations.map(c => c.T),
                y: concentrations.map(c => c.energyBalanceCB),
                mode: 'lines',
                name: 'energy balance',
                line: { color: '#0000FF' }
            };

            const annotations = [
                {
                    x: 360,
                    y: concentrations.find(c => c.T === 360)?.massBalanceCB || 0,
                    text: 'mass balance',
                    showarrow: true,
                    arrowhead: 7,
                    ax: 0,
                    ay: -40,
                    font: { color: '#008000' }
                },
                {
                    x: 280,
                    y: concentrations.find(c => c.T === 280)?.energyBalanceCB || 0,
                    text: 'energy balance',
                    showarrow: true,
                    arrowhead: 7,
                    ax: 0,
                    ay: 40,
                    font: { color: '#0000FF' }
                }
            ];

            const layout = {
                xaxis: { title: 'temperature (K)', range: [250, 400], gridcolor: '#ddd' },
                yaxis: { title: 'product concentration (mmol/dm³)', range: [0, 12], gridcolor: '#ddd' },
                annotations: annotations,
                font: { family: 'sans-serif' },
                plot_bgcolor: '#fff',
                paper_bgcolor: '#fff'
            };

            Plotly.react('plot-container', [massBalanceTrace, energyBalanceTrace], layout);
        }

        document.querySelectorAll('input[type="range"]').forEach(slider => {
            slider.addEventListener('input', updatePlot);
        });

        // Initial plot
        updatePlot();
    </script>
</body>
</html>
