    <script src="https://cdn.jsdelivr.net/npm/mathjax@3/es5/tex-mml-chtml.js"></script>
</head>
<body>
    <div id="container">
        <div id="control-panel">
            <h4>stability of multilayered emulsions</h4>
            <div class="slider-group">
                <label for="slider-phi">\(\phi\)</label>
                <input type="range" id="slider-phi" min="0.0001" max="0.05" step="0.0001" value="0.005">
                <span id="phi-value-display">0.005</span>
            </div>
            <hr>
            <div class="slider-group">
                <label for="slider-gamma-sat">\(\Gamma_{sat}\) (mg/m²)</label>
                <input type="range" id="slider-gamma-sat" min="0.1" max="10" step="0.1" value="1.5">
                <span id="gamma-sat-value-display">1.5</span>
            </div>
            <div class="slider-group">
                <label for="slider-r">r (nm)</label>
                <input type="range" id="slider-r" min="50" max="1000" step="10" value="300">
                <span id="r-value-display">300</span>
            </div>
            <div class="slider-group">
                <label for="slider-rpe">r<sub>pe</sub> (nm)</label>
                <input type="range" id="slider-rpe" min="5" max="50" step="1" value="20">
                <span id="rpe-value-display">20</span>
            </div>
            <div class="slider-group">
                <label for="slider-mw">M<sub>W</sub> (kDa)</label>
                <input type="range" id="slider-mw" min="10" max="1000" step="1" value="166">
                <span id="mw-value-display">166</span>
            </div>
            <hr>
            <div class="slider-group">
                <label for="slider-phi-axis">\(\phi\) axis max.</label>
                <input type="range" id="slider-phi-axis" min="0.01" max="0.5" step="0.01" value="0.05">
                <span id="phi-axis-value-display">0.05</span>
            </div>
            <div class="slider-group">
                <label for="slider-c-axis">C axis max.</label>
                <input type="range" id="slider-c-axis" min="1.0" max="5.0" step="0.1" value="2.0">
                <span id="c-axis-value-display">2.0</span>
            </div>
        </div>
        <div id="vis-area">
            <div id="info-display">
                <p id="formula-display"><span style="color:blue;">y<sub>1</sub>(\(\phi\))</span>, <span style="color:red;">y<sub>2</sub>(\(\phi\))</span>, <span style="color:green;">y<sub>3</sub>(\(\phi\))</span></p>
                <p id="phimax-display">\(\phi_{max}\): 0.000</p>
                <p id="range-display">Stability Range: 0.000 to 0.000</p>
            </div>
            <div id="plot-container"></div>
        </div>
    </div>
    <script>
        const C_UNIT_CONVERSION = 1000;
        const K_DEPLETION = 0.137;

        const sliders = {
            phi: document.getElementById('slider-phi'),
            gammaSat: document.getElementById('slider-gamma-sat'),
            r: document.getElementById('slider-r'),
            rPe: document.getElementById('slider-rpe'),
            mw: document.getElementById('slider-mw'),
            phiAxis: document.getElementById('slider-phi-axis'),
            cAxis: document.getElementById('slider-c-axis')
        };

        const displays = {
            phiValue: document.getElementById('phi-value-display'),
            gammaSatValue: document.getElementById('gamma-sat-value-display'),
            rValue: document.getElementById('r-value-display'),
            rPeValue: document.getElementById('rpe-value-display'),
            mwValue: document.getElementById('mw-value-display'),
            phiAxisValue: document.getElementById('phi-axis-value-display'),
            cAxisValue: document.getElementById('c-axis-value-display'),
            phimaxDisplay: document.getElementById('phimax-display'),
            rangeDisplay: document.getElementById('range-display')
        };

        function updateDisplays() {
            displays.phiValue.textContent = sliders.phi.value;
            displays.gammaSatValue.textContent = sliders.gammaSat.value;
            displays.rValue.textContent = sliders.r.value;
            displays.rPeValue.textContent = sliders.rPe.value;
            displays.mwValue.textContent = sliders.mw.value;
            displays.phiAxisValue.textContent = sliders.phiAxis.value;
            displays.cAxisValue.textContent = sliders.cAxis.value;
        }

        function calculateValues() {
            const gammaSat = parseFloat(sliders.gammaSat.value);
            const r = parseFloat(sliders.r.value);
            const mw = parseFloat(sliders.mw.value);
            const rPe = parseFloat(sliders.rPe.value);

            const A = (3 * gammaSat / r) * C_UNIT_CONVERSION;
            const B = (K_DEPLETION * mw) / (Math.pow(rPe, 3));
            const phiMax = Math.sqrt(B / A);

            displays.phimaxDisplay.textContent = `\\(\\phi_{max}\\): ${phiMax.toFixed(3)}`;
            MathJax.typesetPromise();

            return { phiMax, A, B };
        }

        function generateData(phiMax, A, B) {
            const phiValues = [];
            const y1Values = [];
            const y2Values = [];
            const y3Values = [];

            for (let phi = 0; phi <= parseFloat(sliders.phiAxis.value); phi += 0.001) {
                const y1 = A * phi;
                const y2 = y1 / (1 - phi);
                const y3 = A * phi + (B / phi);

                phiValues.push(phi);
                y1Values.push(y1);
                y2Values.push(y2);
                y3Values.push(y3);
            }

            const shadedPhiValues = phiValues.filter(phi => phi <= phiMax);
            const shadedY2Values = y2Values.slice(0, shadedPhiValues.length);
            const shadedY3Values = y3Values.slice(0, shadedPhiValues.length);

            return {
                phiValues,
                y1Values,
                y2Values,
                y3Values,
                shadedPhiValues,
                shadedY2Values,
                shadedY3Values
            };
        }

        function updatePlot(data, phiMax) {
            const traceY1 = {
                x: data.phiValues,
                y: data.y1Values,
                type: 'scatter',
                mode: 'lines',
                line: { color: 'blue' },
                name: 'y1'
            };

            const traceY2 = {
                x: data.phiValues,
                y: data.y2Values,
                type: 'scatter',
                mode: 'lines',
                line: { color: 'red' },
                name: 'y2'
            };

            const traceY3 = {
                x: data.phiValues,
                y: data.y3Values,
                type: 'scatter',
                mode: 'lines',
                line: { color: 'green' },
                name: 'y3'
            };

            const traceShadedRegion = {
                x: [...data.shadedPhiValues, ...data.shadedPhiValues.slice().reverse()],
                y: [...data.shadedY2Values, ...data.shadedY3Values.slice().reverse()],
                type: 'scatter',
                mode: 'lines',
                fill: 'toself',
                fillcolor: 'rgba(173, 216, 230, 0.5)',
                showlegend: false
            };

            const traceMarkers = {
                x: [parseFloat(sliders.phi.value)],
                y: [data.y1Values[Math.floor(parseFloat(sliders.phi.value) / 0.001)], data.y2Values[Math.floor(parseFloat(sliders.phi.value) / 0.001)], data.y3Values[Math.floor(parseFloat(sliders.phi.value) / 0.001)]],
                type: 'scatter',
                mode: 'markers',
                marker: { color: ['blue', 'red', 'green'] }
            };

            const layout = {
                xaxis: { title: '\\(\\phi\\) (volume fraction)', range: [0, parseFloat(sliders.phiAxis.value)] },
                yaxis: { title: 'C (kg/m³)', range: [0, parseFloat(sliders.cAxis.value)] },
                shapes: [{
                    type: 'line',
                    x0: parseFloat(sliders.phi.value),
                    y0: 0,
                    x1: parseFloat(sliders.phi.value),
                    y1: parseFloat(sliders.cAxis.value),
                    line: { color: 'grey', dash: 'dash' }
                }],
                showlegend: false
            };

            Plotly.react('plot-container', [traceY1, traceY2, traceY3, traceShadedRegion, traceMarkers], layout);
        }

        function updateRangeDisplay(data) {
            const phiVal = parseFloat(sliders.phi.value);
            const index = Math.floor(phiVal / 0.001);
            const lowerBound = data.y2Values[index];
            const upperBound = data.y3Values[index];
            displays.rangeDisplay.textContent = `Stability Range: ${lowerBound.toFixed(3)} to ${upperBound.toFixed(3)}`;
        }

        function recalculateAndRender() {
            updateDisplays();
            const { phiMax, A, B } = calculateValues();
            const data = generateData(phiMax, A, B);
            updatePlot(data, phiMax);
            updateRangeDisplay(data);
        }

        sliders.phi.addEventListener('input', () => {
            updateDisplays();
            const { A, B } = calculateValues();
            const data = generateData(parseFloat(sliders.phiAxis.value), A, B);
            Plotly.restyle('plot-container', 'x[4]', [parseFloat(sliders.phi.value)]);
            Plotly.restyle('plot-container', 'y[4]', [
                data.y1Values[Math.floor(parseFloat(sliders.phi.value) / 0.001)],
                data.y2Values[Math.floor(parseFloat(sliders.phi.value) / 0.001)],
                data.y3Values[Math.floor(parseFloat(sliders.phi.value) / 0.001)]
            ]);
            Plotly.relayout('plot-container', 'shapes[0].x0', parseFloat(sliders.phi.value));
            Plotly.relayout('plot-container', 'shapes[0].x1', parseFloat(sliders.phi.value));
            updateRangeDisplay(data);
        });

        sliders.phiAxis.addEventListener('input', () => {
            updateDisplays();
            sliders.phi.max = parseFloat(sliders.phiAxis.value);
            recalculateAndRender();
        });

        sliders.cAxis.addEventListener('input', () => {
            updateDisplays();
            Plotly.relayout('plot-container', 'yaxis.range', [0, parseFloat(sliders.cAxis.value)]);
            Plotly.relayout('plot-container', 'shapes[0].y1', parseFloat(sliders.cAxis.value));
        });

        sliders.gammaSat.addEventListener('input', recalculateAndRender);
        sliders.r.addEventListener('input', recalculateAndRender);
        sliders.rPe.addEventListener('input', recalculateAndRender);
        sliders.mw.addEventListener('input', recalculateAndRender);

        window.onload = recalculateAndRender;
    </script>
</body>
</html>
