            background-color: #aaa;
            border-radius: 50%;
            position: absolute;
            top: 30px;
            left: 30px;
            cursor: pointer;
        }
        .slider-label {
            display: block;
            margin-top: 10px;
        }
        .slider-value {
            display: block;
            margin-bottom: 10px;
        }
    </style>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
</head>
<body>
    <div id="main-container">
        <div id="control-panel">
            <label for="slider-time" class="slider-label">time relative to collision (s)</label>
            <input type="range" id="slider-time" min="-30" max="30" step="0.1" value="-10">
            <span id="label-time" class="slider-value">-10</span>

            <h4>object 1</h4>
            <label for="slider-m1" class="slider-label">mass (kg)</label>
            <input type="range" id="slider-m1" min="1" max="10" step="0.01" value="5">
            <span id="label-m1" class="slider-value">5</span>
            <label for="slider-v1" class="slider-label">speed (m/s)</label>
            <input type="range" id="slider-v1" min="1" max="20" step="0.1" value="10">
            <span id="label-v1" class="slider-value">10</span>

            <h4>object 2</h4>
            <label for="slider-m2" class="slider-label">mass (kg)</label>
            <input type="range" id="slider-m2" min="1" max="10" step="0.01" value="3">
            <span id="label-m2" class="slider-value">3</span>
            <label for="slider-v2" class="slider-label">speed (m/s)</label>
            <input type="range" id="slider-v2" min="1" max="20" step="0.1" value="5">
            <span id="label-v2" class="slider-value">5</span>

            <label>observer velocity (m/s)</label>
            <div id="joystick-container">
                <div id="joystick-knob"></div>
            </div>
            <span id="label-v0x">v0x: 1.1</span>
            <span id="label-v0y">v0y: 1.1</span>
        </div>
        <div id="canvas-container"></div>
    </div>

    <script>
        let m1 = 5, m2 = 3, v1 = 10, v2 = 5;
        let v0x = 1.1, v0y = 1.1;
        let time = -10;

        function setup() {
            const canvas = createCanvas(windowWidth - 300, windowHeight);
            canvas.parent('canvas-container');
            document.getElementById('slider-time').addEventListener('input', updateSimulation);
            document.getElementById('slider-m1').addEventListener('input', updateSimulation);
            document.getElementById('slider-v1').addEventListener('input', updateSimulation);
            document.getElementById('slider-m2').addEventListener('input', updateSimulation);
            document.getElementById('slider-v2').addEventListener('input', updateSimulation);

            const joystickContainer = document.getElementById('joystick-container');
            const joystickKnob = document.getElementById('joystick-knob');
            let isDragging = false;

            joystickContainer.addEventListener('mousedown', () => isDragging = true);
            document.addEventListener('mouseup', () => isDragging = false);
            document.addEventListener('mousemove', (e) => {
                if (!isDragging) return;
                const rect = joystickContainer.getBoundingClientRect();
                let x = e.clientX - rect.left - joystickKnob.offsetWidth / 2;
                let y = e.clientY - rect.top - joystickKnob.offsetHeight / 2;
                const maxDistance = rect.width / 2 - joystickKnob.offsetWidth / 2;
                const distance = Math.sqrt(x * x + y * y);
                if (distance > maxDistance) {
                    x = (x / distance) * maxDistance;
                    y = (y / distance) * maxDistance;
                }
                joystickKnob.style.left = `${x}px`;
                joystickKnob.style.top = `${y}px`;
                v0x = map(x, -maxDistance, maxDistance, -5, 5);
                v0y = map(y, -maxDistance, maxDistance, 5, -5);
                document.getElementById('label-v0x').innerText = `v0x: ${v0x.toFixed(2)}`;
                document.getElementById('label-v0y').innerText = `v0y: ${v0y.toFixed(2)}`;
                updateSimulation();
            });

            updateSimulation();
        }

        function draw() {
            background(255);
            strokeWeight(2);

            // Calculate velocities in CM frame
            const s1_cm = v1;
            const s2_cm = (m1 / m2) * s1_cm;
            const u1_cm = createVector(s1_cm, 0);
            const u2_cm = createVector(-s2_cm, 0);
            const v1_cm = u1_cm.copy().rotate(-PI / 4);
            const v2_cm = u2_cm.copy().rotate(-PI / 4);

            // Observer frame velocities
            const v0 = createVector(v0x, v0y);
            const u1_obs = p5.Vector.add(u1_cm, v0);
            const u2_obs = p5.Vector.add(u2_cm, v0);
            const v1_obs = p5.Vector.add(v1_cm, v0);
            const v2_obs = p5.Vector.add(v2_cm, v0);

            // Positions
            const pos1 = u1_obs.copy().mult(time);
            const pos2 = u2_obs.copy().mult(time);

            // Trajectories
            noFill();
            stroke(255, 0, 0);
            drawingContext.setLineDash([5, 5]);
            line(-width, 0, width, 0);
            stroke(0, 0, 255);
            line(0, -height, 0, height);
            drawingContext.setLineDash([]);

            // Particles
            fill(255, 0, 0);
            circle(pos1.x, pos1.y, 10 * sqrt(m1));
            fill(0, 0, 255);
            circle(pos2.x, pos2.y, 10 * sqrt(m2));

            // Observer velocity vector
            stroke(0);
            line(0, 0, -v0.x * 20, -v0.y * 20);
            triangle(-v0.x * 20, -v0.y * 20, -v0.x * 20 + 5, -v0.y * 20 + 10, -v0.x * 20 + 5, -v0.y * 20 - 10);

            // Text display
            noStroke();
            fill(0);
            textSize(16);
            text(`v₀: ${(v0.mag()).toFixed(2)} m/s`, 10, 20);
            text(`v₁: ${(u1_obs.mag()).toFixed(2)} m/s`, 10, 40);
            text(`v₂: ${(u2_obs.mag()).toFixed(2)} m/s`, 10, 60);
            text(`Energy: ${(0.5 * m1 * u1_obs.magSq() + 0.5 * m2 * u2_obs.magSq()).toFixed(2)} J`, 10, 80);
        }

        function updateSimulation() {
            time = parseFloat(document.getElementById('slider-time').value);
            m1 = parseFloat(document.getElementById('slider-m1').value);
            v1 = parseFloat(document.getElementById('slider-v1').value);
            m2 = parseFloat(document.getElementById('slider-m2').value);
            v2 = parseFloat(document.getElementById('slider-v2').value);

            document.getElementById('label-time').innerText = `time relative to collision (s): ${time}`;
            document.getElementById('label-m1').innerText = `mass (kg): ${m1}`;
            document.getElementById('label-v1').innerText = `speed (m/s): ${v1}`;
            document.getElementById('label-m2').innerText = `mass (kg): ${m2}`;
            document.getElementById('label-v2').innerText = `speed (m/s): ${v2}`;

            redraw();
        }

        function map(value, start1, stop1, start2, stop2) {
            return ((value - start1) / (stop1 - start1)) * (stop2 - start2) + start2;
        }
    </script>
</body>
</html>
