    function encrypt(text, shift) {
        return text
            .split('')
            .map(char => {
                if (/[a-z]/.test(char)) {
                    const base = char <= 'm' ? 'a' : 'n';
                    return String.fromCharCode((char.charCodeAt(0) - base.charCodeAt(0) + shift) % 26 + base.charCodeAt(0));
                }
                return char;
            })
            .join('');
    }

    function updateCipher() {
