        for (let i = 0; i < 26; i++) {
            const shiftIndex = (i + shiftValue) % 26;
            const letter = String.fromCharCode(65 + shiftIndex);
            const span = document.createElement('span');
            span.textContent = letter;
            ciphertextAlphabet.appendChild(span);
        }

        // Update key displays
        document.getElementById('encryption-key-display').textContent = `encryption key: ${shiftValue}`;
        const decryptionKey = (26 - shiftValue + 26) % 26;
        document.getElementById('decryption-key-display').textContent = `decryption key: ${decryptionKey}`;

        // Update text outputs
        document.getElementById('plaintext-output').textContent = `plaintext : ${textInput}`;
        document.getElementById('ciphertext-output').textContent = `ciphertext: ${encrypt(textInput, shiftValue)}`;
    }

