                const offset = parseInt(document.getElementById('slider-offset').value);

                // Enable/disable controls based on cipher
                document.getElementById('input-keyword').disabled = (cipher === 'Caesar');
                document.getElementById('slider-offset').disabled = (cipher !== 'Caesar');

                // Set offset value display
                document.getElementById('offset-value').textContent = offset;

                // Process plaintext
                let processedText = message === 'DeclarationOfIndependence' ? originalText : "Four score and seven years ago our fathers brought forth on this continent a new nation, conceived in liberty, and dedicated to the proposition that all men are created equal.";
