                document.getElementById('original-text').textContent = processedText.slice(0, 80) + "...";

                // Calculate letter frequency
                const freqMap = {};
                processedText.split('').forEach(char => {
                    freqMap[char] = (freqMap[char] || 0) + 1;
                });
                let freqArray = Object.entries(freqMap).map(([char, count]) => [char, (count / processedText.length) * 100]);

                // Sort frequencies in descending order
                freqArray.sort((a, b) => b[1] - a[1]);

                // Prepare data for Plotly bar chart
                const barData = {
                    type: 'bar',
                    x: freqArray.map(entry => entry[0]),
                    y: freqArray.map(entry => entry[1]),
                    marker: { color: 'orange' }
