                            y0: 100 / 26,
                            y1: 100 / 26,
                            line: { color: 'red', width: 2, dash: 'dash' }
                        }
                    ]
                };

                // Render Plotly charts
                Plotly.newPlot('original-chart', [barData], barLayout);
                Plotly.newPlot('encrypted-chart', [barData], barLayout); // Placeholder for encrypted chart

                // Perform encryption based on selected cipher
                if (cipher === 'Caesar') {
                    encryptedText = processedText.split('').map(char => {
                        const code = char.charCodeAt(0) + offset;
                        return String.fromCharCode(code > 122 ? code - 26 : code);
                    }).join('');
                } else if (cipher === 'De Vigenere') {
                    encryptedText = processedText.split('').reduce((acc, char, i) => {
                        const keyChar = keyword[i % keyword.length];
                        if (/[a-z]/.test(keyChar)) {
                            const keyInt = keyChar.charCodeAt(0) - 97;
                            const shift = char.charCodeAt(0) - keyChar.charCodeAt(0);
                            return acc + String.fromCharCode((shift + 26) % 26 + 97);
                        }
                        return acc + char;
                    }, '');
                } else if (cipher === 'Autokey') {
                    encryptedText = processedText.split('').reduce((acc, char, i) => {
                        const keyChar = i === 0 ? keyword : (acc[i - 1] || keyChar);
                        if (/[a-z]/.test(keyChar)) {
                            const keyInt = keyChar.charCodeAt(0) - 97;
                            const shift = char.charCodeAt(0) - keyChar.charCodeAt(0);
                            return acc + String.fromCharCode((shift + 26) % 26 + 97);
                        }
                        return acc + char;
                    }, '');
                }

                // Display encrypted text
                document.getElementById('encrypted-text').textContent = encryptedText;
            }

            // Add event listeners
            document.getElementById('select-message').addEventListener('change', updateVisualization);
            document.getElementById('select-cipher').addEventListener('change', updateVisualization);
            document.getElementById('input-keyword').addEventListener('input', updateVisualization);
            document.getElementById('slider-offset').addEventListener('input', () => {
                updateVisualization();
                document.getElementById('offset-value').textContent = document.getElementById('slider-offset').value;
            });

