            const valueB = document.getElementById('value-b');

            let a = parseFloat(sliderA.value);
            let b = parseFloat(sliderB.value);
            let order = parseInt(sliderOrder.value);

            updateValueDisplays();

            function updateValueDisplays() {
                valueOrder.textContent = sliderOrder.value + ".0";
                valueA.textContent = sliderA.value.toFixed(1);
                valueB.textContent = sliderB.value.toFixed(1);
            }

            function calculatePowerSeriesCoefficients(order) {
                const f_derivs = [a, b];
                let fxx = -(f_derivs[1] + 1.5 * f_derivs[0]) / f_derivs[0];
                f_derivs[2] = fxx;
                for (let i = 3; i <= order; i++) {
                    let temp = -(i * (i - 1) * fxx + (i - 1) * b + 1.5 * i * fxx) / f_derivs[0];
                    fxx = temp;
                    f_derivs[i] = temp;
                }
                const coefficients = f_derivs.map(coefficient => coefficient / factorial(i));
                return coefficients;
            }

            function factorial(n) {
                let result = 1;
                for (let i = 2; i <= n; i++) {
                    result *= i;
                }
                return result;
            }

            function generatePowerSeriesData(order) {
                const xValues = Array.from({ length: 200 }, (_, i) => i / 100);
                const fApproxX = xValues.map(x => {
                    let sum = 0;
                    const coeffs = calculatePowerSeriesCoefficients(order);
                    for (let k = 0; k <= order; k++) {
                        sum += coeffs[k] * Math.pow(x, k);
                    }
                    return sum;
                });
                return xValues.map((x, index) => ({ x, y: fApproxX[index] }));
            }

            function rungeKutta4(dydx, x0, y0, h, n) {
                const y = [];
                y[0] = y0;
                for (let i = 1; i <= n; i++) {
                    const k1 = dydx(x0, y0);
                    const k2 = dydx(x0 + h / 2, y0 + h / 2 * k1);
                    const k3 = dydx(x0 + h / 2, y0 + h / 2 * k2);
                    const k4 = dydx(x0 + h, y0 + h * k3);
                    y[i] = y0 + h / 6 * (k1 + 2 * k2 + 2 * k3 + k4);
                    x0 += h;
                    y0 = y[i];
                }
                return y;
            }

            function generateNumericalSolutionData(a, b) {
                const dydx = (x, y) => [y[1], -(y[1] + 1.5 * y[0]) / y[0]];
                const xValues = Array.from({ length: 200 }, (_, i) => i / 100);
                const y0 = [a, b];
                const h = xValues[1] - xValues[0];
                const n = xValues.length - 1;
                const fNumericalX = rungeKutta4(dydx, xValues[0], y0, h, n).map(y => y[0]);
                return xValues.map((x, index) => ({ x, y: fNumericalX[index] }));
            }

            function determineAgreementRegion(fApproxX, fNumericalX) {
                for (let i = 0; i < fApproxX.length; i++) {
                    if (Math.abs(fApproxX[i] - fNumericalX[i]) >= 0.01) {
                        return xValues[i - 1];
                    }
                }
                return xValues[xValues.length - 1];
            }

            function generatePolynomialString(coefficients) {
                let str = '';
                coefficients.forEach((coeff, index) => {
                    if (Math.abs(coeff) < 1e-5) return;
                    if (index > 0) str += (str ? ' ' : '') + (coeff < 0 ? '-' : '+');
                    if (Math.abs(coeff) !== 1 || index === 0) str += Math.abs(coeff).toFixed(5);
                    if (index > 0) str += 'x';
                    if (index > 1) str += `^${index}`;
