    <script src="https://cdn.plot.ly/plotly-latest.min.js"></script>
    <script>
        let x_coords = [];
        let y_coords = [];
        let z_values = [];

        // Default parameters
        let dip = 0;
        let depth = 500;
        let limit = 1000;
        let component = 'z';
        let faultType = 'tensile';

        function calculateDisplacements(x, y, dip, depth, faultType) {
            const b = 1; // dislocation magnitude
            const nu = 0.25; // Poisson's ratio
            dip = dip * Math.PI / 180; // convert dip to radians
            let ux, uy, uz;

            switch (faultType) {
                case 'tensile':
                    ux = x * b * (1 - 2 * nu) / (depth * depth * depth);
                    uy = y * b * (1 - 2 * nu) / (depth * depth * depth);
                    uz = (x * x + y * y) * b * (1 - 2 * nu) / (2 * depth * depth * depth);
                    break;
                case 'strike-slip':
                    ux = x * b * (1 - 2 * nu) / (depth * depth * depth);
                    uy = y * b * (1 - 2 * nu) / (depth * depth * depth);
                    uz = 0;
                    break;
                case 'normal':
                    ux = x * b * (1 - 2 * nu) / (depth * depth * depth);
                    uy = y * b * (1 - 2 * nu) / (depth * depth * depth);
                    uz = x * y * b * (1 - 2 * nu) / (depth * depth * depth);
                    break;
            }

            if (component === 'x') return { ux, uy, uz };
            if (component === 'y') return { ux, uy, uz };
            if (component === 'z') return { ux, uy, uz };
        }

        function generateGrid(limit) {
            const res = 50;
            let x_coords = [];
            let y_coords = [];
            for (let i = -limit; i <= limit; i += limit / res) {
                x_coords.push(i);
            }
            for (let j = -limit; j <= limit; j += limit / res) {
                y_coords.push(j);
            }
            return { x_coords, y_coords };
        }

        function updatePlot(component, faultType) {
            let params = {
                dip: getSliderValue('slider-dip'),
                depth: getSliderValue('slider-depth'),
                limit: getSliderValue('slider-limit'),
                faultType: faultType || getFaultType(),
                component: component || getComponent(),
            };

            const { x_coords, y_coords } = generateGrid(params.limit);
            z_values = new Array(params.limit * 2 + 1).fill().map(() => new Array(params.limit * 2 + 1).fill(NaN));

