<body>
    <div id="app-container">
        <div id="control-panel">
            <div>
                <label for="slider-t">t</label>
                <input type="range" id="slider-t" min="0" max="4" step="0.01" value="0.1">
                <span id="t-value-display">0.10</span>
            </div>
            <div>
                <label for="slider-k">k</label>
                <input type="range" id="slider-k" min="1" max="10" step="1" value="2">
                <span id="k-value-display">2</span>
            </div>
            <div>
                <label for="checkbox-axes">axes</label>
                <input type="checkbox" id="checkbox-axes" checked>
            </div>
        </div>
        <div id="visualization-area">
            <div id="formula-display"></div>
            <div id="canvas-container"></div>
        </div>
    </div>

    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
    <script>
        let w, h;
        let sliderT, sliderK;
        let timeFactor = 0.1;
        let mode = 2;
        let omega_k;
        let zeros = [2.4048, 5.5201, 8.6537, 11.7915, 14.9309, 18.0711, 21.2116, 24.3525, 27.4935, 30.6346];
        const amplitude = 0.8;
        let colorString = color('#0074D9'); // Medium blue

        function setup() {
            w = 420;
            h = 300;
            let mY = 80;
            let canvas = createCanvas(w, h);
            canvas.parent('canvas-container');
            sliderT = createSlider(0, 4, 0.1);
            sliderT.parent('control-panel');
            sliderK = createSlider(1, 10, 2);
            sliderK.parent('control-panel');
            attachEventListeners();
            plotFormula();
        }

        function draw() {
            clear();
            if (checkboxAxes.checked) {
                drawAxes();
                drawTicksAndLabels();
            }
            calculateParameters();
            drawString();
        }

        function attachEventListeners() {
            sliderT.input(() => {
                timeFactor = sliderT.value();
                tValueDisplay.html(timeFactor.toFixed(2));
                redraw();
            });

            sliderK.input(() => {
                mode = sliderK.value();
                kValueDisplay.html(mode);
                omega_k = zeros[mode - 1] / 2;
                plotFormula();
                redraw();
            });

            $('#checkbox-axes').change(() => {
                checkboxAxes.checked = $('#checkbox-axes').prop('checked');
                redraw();
            });
        }

        function calculateParameters() {
            omega_k = zeros[mode - 1] / 2;
            timeFactor = cos(omega_k * timeFactor);
        }

        function draw() {
            clear();
            if (checkboxAxes.checked) {
                drawAxes();
                drawTicksAndLabels();
            }
            calculateParameters();
            drawString();
        }

        function drawString() {
            beginShape();
            noFill();
            stroke(colorString);
            strokeWeight(2);
            for (let yNrm = 0; yNrm <= 1; yNrm += 0.01) {
                let besselArg = zeros[mode - 1] * sqrt(yNrm);
                let x_pos = amplitude * timeFactor * J0(besselArg);
                let y_pos = -yNrm;
                let xPxl = map(x_pos, -2, 2, 0, w);
                let yPxl = map(y_pos, -1.2, 0, h, 0);
                vertex(xPxl, yPxl);
            }
            endShape();
        }

        function drawAxes() {
            push();
            stroke(150);
            strokeWeight(1);
            line(0, 0, 0, h);
            line(0, 0, w, 0);
            pop();
        }

        function drawTicksAndLabels() {
            drawXAxisTicks();
            drawYAxisTicks();
        }

        function drawXAxisTicks() {
            let tickLength = 10;
            let tickSpacing = 0.2;
            for (let xPxl = -200; xPxl <= 200; xPxl += 0.2 * w) {
                let x = map(xPxl, 0, w, -2, 2);
                fill(150);
                textSize(12);
                text(x.toFixed(1), xPxl + 10, h - 20);
                stroke(150);
                strokeWeight(1);
                line(xPxl, 0, xPxl, tickLength);
            }
        }

        function drawYAxisTicks() {
            let tickLength = 10;
            let tickSpacing = 0.1;
            for (let yPxl = 0; yPxl <= h * 1.2; yPxl += tickSpacing * h) {
                let y = map(yPxl, 0, h, -1.2, 0);
                fill(150);
                textSize(10);
                text(y.toFixed(1), -30, yPxl);
                stroke(150);
                strokeWeight(1);
                line(0, yPxl, tickLength, yPxl);
            }
        }

        function J0(x) {
            return 1 - x * x / 4 + pow(x, 4) * (7 - 90 * x * x) / 768 - pow(x, 6) * (127 - 3150 * x * x) / 221184 + pow(x, 8) * (44149 - 1971300 * x * x) / 554268288;
        }

        function plotFormula() {
            let latex = `\\(\\omega_{${mode}} = ${omega_k.toFixed(2)} \\sqrt{g/l}\\)`;
            MathJax.typesetPromise([latex]).then(() => {
                $('#formula-display').html(`${latex}`);
            });
        }
    </script>
</body>

