    <script>
        let currentStep = 1;
        let pointP = {
            x: 240, // Initial x position
            y: 312  // Initial y position
        };

        function setup() {
            let cnv = createCanvas(600, 520);
            cnv.parent('canvas-container');
            noLoop();
        }

        function draw() {
            background(255);
            drawMainTriangle();
            drawMainAltitude();
            drawPointP();
            drawPerpendiculars(currentStep);
            drawStepSpecific(currentStep);
        }

        function drawMainTriangle() {
            beginShape();
            vertex(width / 2, 0);
            vertex(0, height);
            vertex(width, height);
            endShape(CLOSE);
            text("A", width / 2 + 5, 10);
            text("B", 0, height - 5);
            text("C", width - 5, height - 5);
        }

        function drawMainAltitude() {
            let midX = width / 2;
            let midY = height / 2;
            stroke(0, 192, 192);
            strokeWeight(1);
            line(midX, 0, midX, height);
            stroke(0);
            strokeWeight(1);
        }

        function drawPointP() {
            let pX = pointP.x;
            let pY = pointP.y;
            strokeWeight(1);
            stroke(0);
            point(pX, pY);
            stroke(153); // Gray
            fill(153);
            ellipse(pX, pY, 10, 10);
            fill(0);
            text("P", pX + 5, pY + 5);
        }

        function drawPerpendiculars(step) {
            if (step === 1) {
                stroke(0);
                strokeWeight(2);
                line(pointP.x, pointP.y, pointP.x, 0);
                line(pointP.x, pointP.y, pointP.x, height);
                line(pointP.x, pointP.y, width / 2, pointP.y);
                noStroke();
                strokeWeight(1);
            }
        }

        function drawStepSpecific(step) {
            if (step === 1) {
                let pX = pointP.x;
                let pY = pointP.y;
                stroke(0);
                strokeWeight(1);
                fill(0);
                ellipse(pX - 50, pY - 50, 10, 10); // Foot on AB
                ellipse(pX + 50, pY - 50, 10, 10); // Foot on BC
                ellipse(pX + 50, pY + 50, 10, 10); // Foot on AC
            } else if (step === 2) {
                let redHeight = (pY - height / 2) * 2 / sqrt(3);
                let greenHeight = (pY - height / 2) * 2 / sqrt(3);
                let blueHeight = (pY - height / 2) * 2 / sqrt(3);
