                triangle(pX, pY, pX + blueHeight * sqrt(3), pY - blueHeight, pX - blueHeight * sqrt(3), pY - blueHeight);
                strokeWeight(1);
            } else if (step === 3) {
                let redHeight = (pY - height / 2) * 2 / sqrt(3);
                let blueHeight = (pY - height / 2) * 2 / sqrt(3);
                let greenHeight = (pY - height / 2) * 2 / sqrt(3);
                stroke(255, 0, 0);
                strokeWeight(1);
                fill(255, 0, 0, 128);
                triangle(width / 2 - redHeight * sqrt(3), height / 2, width / 2 + redHeight * sqrt(3), height / 2, width / 2, height / 2 - redHeight);
                stroke(0, 0, 255);
                strokeWeight(1);
                fill(0, 0, 255, 128);
                line(width / 2 - blueHeight * sqrt(3), height / 2, width / 2 + blueHeight * sqrt(3), height / 2);
                line(width / 2 - blueHeight * sqrt(3), height / 2, width / 2 - blueHeight * sqrt(3), height / 2 - blueHeight);
                line(width / 2 + blueHeight * sqrt(3), height / 2, width / 2 + blueHeight * sqrt(3), height / 2 - blueHeight);
                stroke(0, 255, 0);
                strokeWeight(1);
                fill(0, 255, 0, 128);
                line(width / 2 - blueHeight * sqrt(3), height / 2 - blueHeight, width / 2 + blueHeight * sqrt(3), height / 2 - blueHeight);
                line(width / 2 - blueHeight * sqrt(3), height / 2 - blueHeight, width / 2 + blueHeight * sqrt(3), height / 2 - blueHeight);
                var x = width / 2 - blueHeight * sqrt(3);
                var y = height / 2 - blueHeight;
                var z = height / 2 - blueHeight;
                text("C", width / 2 - 5, height / 2 - 5);
            }
        }

        function mouseDragged() {
            let mouseX = constrain(mouseX, 10, width - 10);
            let mouseY = constrain(mouseY, 10, height - 10);
            pointP.x = mouseX;
            pointP.y = mouseY;
